[[module css]]

#page-title { display: none; }
.feature-block {
    height: 260px;
}

.section {
    background: linear-gradient(to bottom, #eee 0%, #eee 50%, #fff 50%, #fff 100%);
    border: 1px solid #888;
    border-radius: 10px;
    overflow: hidden;
    margin-bottom: 15px;}
.section > p {
    display: none;}
.section h3 {
    display: inline-block;
    float: left;
    padding-left: 0.7em;
    margin-top: none;
    margin-bottom: none;
    font-size: 1.4em;
    font-weight: bolder;
    color: #901;}
.section > a {
    float: right;
    font-size: 1.4em;
    font-weight: bolder;
    padding-right: 0.7em;
    margin-top: 0.5em;}
.section table {
    width: 100%;}
.section table * {
    text-align: left;}
.section table td, .section table th {
    border: none;}
.section table td:first-child {
    font-weight: bold;}
.section table tr{
    background: #fff;}
.section table tr:nth-child(odd){
    border-bottom: 1px dashed #888;}
.section table tr:nth-child(even){
    border-bottom: 1px dashed #888;}
.section table tr:first-child{
    border-bottom: 1px solid #888;}
.section table tr:last-child {
    border-bottom: none;}
.section .printuser img{
    width: 16px !important;}
[[/module]]

