[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Baskervville+SC&display=swap');

.main{
    text-decoration: none!important;
}

.main:hover {
    text-decoration: none!important;
}
.main:visited {     
    color: white;
}

#page-title{display:none}

#page-content div.unga {
  max-width: 150%;
}

#content-wrap {
background-color: #1f1f1f;
background-image: url("data:image/svg+xml,%3Csvg width='80' height='88' viewBox='0 0 80 88' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M22 21.91V26h-2c-9.94 0-18 8.06-18 18 0 9.943 8.058 18 18 18h2v4.09c8.012.722 14.785 5.738 18 12.73 3.212-6.99 9.983-12.008 18-12.73V62h2c9.94 0 18-8.06 18-18 0-9.943-8.058-18-18-18h-2v-4.09c-8.012-.722-14.785-5.738-18-12.73-3.212 6.99-9.983 12.008-18 12.73zM54 58v4.696c-5.574 1.316-10.455 4.428-14 8.69-3.545-4.262-8.426-7.374-14-8.69V58h-5.993C12.27 58 6 51.734 6 44c0-7.732 6.275-14 14.007-14H26v-4.696c5.574-1.316 10.455-4.428 14-8.69 3.545 4.262 8.426 7.374 14 8.69V30h5.993C67.73 30 74 36.266 74 44c0 7.732-6.275 14-14.007 14H54zM42 88c0-9.94 8.06-18 18-18h2v-4.09c8.016-.722 14.787-5.738 18-12.73v7.434c-3.545 4.262-8.426 7.374-14 8.69V74h-5.993C52.275 74 46 80.268 46 88h-4zm-4 0c0-9.943-8.058-18-18-18h-2v-4.09c-8.012-.722-14.785-5.738-18-12.73v7.434c3.545 4.262 8.426 7.374 14 8.69V74h5.993C27.73 74 34 80.266 34 88h4zm4-88c0 9.943 8.058 18 18 18h2v4.09c8.012.722 14.785 5.738 18 12.73v-7.434c-3.545-4.262-8.426-7.374-14-8.69V14h-5.993C52.27 14 46 7.734 46 0h-4zM0 34.82c3.213-6.992 9.984-12.008 18-12.73V18h2c9.94 0 18-8.06 18-18h-4c0 7.732-6.275 14-14.007 14H14v4.696c-5.574 1.316-10.455 4.428-14 8.69v7.433z' fill='%23ffda00' fill-opacity='0.08' fill-rule='evenodd'/%3E%3C/svg%3E");
}

.main {
    display: block;
    height: 26em;
    width: 18em;
    padding: 0.75rem;
    border: 4mm ridge #523009;
    transition: all 200ms ease-in-out;
    background-position: 30% 50%;
    background-size: cover;
    margin: 0.5em 1em;
    float: left;
}

.main:hover {
    height: 27em;
    width: 19em;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(230, 204, 100,0.5);
    mix-blend-mode: none;
}

.title {
    font-size: 115%;
    color: #e6cc64;
    position: relative;
    top: 18em;
    font-weight: bold;
    background-color: #6e400c;
    padding: 1px 10px;
    display: flex;
    justify-content: center;
    border: 6px ridge #523009;
    text-align: center;
    line-height: 16.5px;
    font-family: "Baskervville SC", serif;
}

.unga {
    display: flex;
    justify-content: center;
    width: 150%;
    flex-flow: wrap;
    margin: 50px;
    align-items: center;
    margin-left: -12em;
    margin-top: -4em;
}

img {
 margin-top: 1em;
}

@media only screen and (max-width: 26.5625rem) {
.unga {
    margin-left: -6em;
}

.main {
    height: 17em;
    width: 9em;
    padding: 0.75rem;
    border: 2mm ridge #523009;
}

.title {
    font-size: 55%;
    top: 25em;
    border: 6px ridge #523009;
    text-align: center;
    line-height: 6.5px;
    font-family: "Baskervville SC", serif;
}

img {
 margin-top: 10em;
}

}

