[[module CSS]]
:root {
     --header-title: "Department of Unreality";
     --header-subtitle: "Making the Unreal a ㅤㅤㅤㅤ";
     --lgurl: url('http://scp-wiki.wikidot.com/local--files/unreality-hub/Unreality%20Header%20Logo.svg');
}

:root {
--min-font-size: 0.9375rem;
--max-font-size: 0.9375rem;
}

#page-content p {
text-align: justify;
margin-top: .6em;
margin-bottom: 1.35em;
}

#main-content hr {
width:33%;
border-bottom: 1px solid black;
margin: auto;
}

.related-flex {
    display: flex;
    justify-content: center;
}
.related {
    box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, 0.7);
    background: #191919;
    padding: 0 1em;
    margin: 1em 0;
}

div #page-title {
display: none;
}

.licensebox p {
display: block;
text-align: left !important;
}

div.page-rate-widget-box .rate-points {
    background-color: white;
    border: none;
}
