[[module CSS]]
:root {
    --backdrop: url(https://scp-wiki.wikidot.com/local--files/drapricus-author-page/sunshine);
}

/* FORKED FROM ADMONITION THEME */

@supports not (-webkit-touch-callout: none) {
    @media only screen and (min-width: 1025px) {
    body {
    background: linear-gradient(90deg, rgba(0, 0, 0, 0) 0%, rgb(var(--basalt-primary-color)) 25%, rgb(var(--basalt-primary-color)) 50%, rgb(var(--basalt-primary-color)) 75%, rgba(0, 0, 0, 0) 100%), var(--backdrop);
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-position: 100% 50%;
    }
    }
}
span.stretch {
    display:inline-block;
    -webkit-transform:scale(2,1); /* Safari and Chrome */
    -moz-transform:scale(2,1); /* Firefox */
    -ms-transform:scale(2,1); /* IE 9 */
    -o-transform:scale(2,1); /* Opera */
    transform:scale(2,1); /* W3C */
}

/* FORKED FROM AUTHOR PAGE TOOL */
.au-preview{
 color:grey;
 display: inline-block;
 width: 95.5%;
 padding-left:2%;
 padding-right:2%;
 margin-bottom:2%;
 border: solid 2px rgb(200, 200, 200);
 transition: box-shadow 1s ease-out;
 background: #fff
}
.au-preview:hover{
 box-shadow: 0px 4px 10px 0 rgba(0, 0, 0, 0.19);
 transition: box-shadow 0.3s ease-out;
 border: solid 2px rgb(150, 150, 150);
 background: #fff url('https://scp-wiki.wdfiles.com/local--files/component%3Aauthor-page/scpf-seal.png') 80% 50% no-repeat;
}
.feature{
 color:grey;
 display: inline-block;
 width: 93%;
 padding-left:3%;
 padding-right:3%;
 border: solid 2px rgb(200, 200, 200);
 transition: box-shadow 1s ease-out;
 background: rgba(250, 251, 255, 0.9) url('https://scp-wiki.wikidot.com/local--files/drapricus-author-page/sidepic') 100% 25% no-repeat;
}
.feature:hover{
 box-shadow: 0px 4px 10px 0 rgba(0, 0, 0, 0.19);
 transition: box-shadow 0.3s ease-out;
}
#page-options-container {
 background: transparent;
 position: relative;
 z-index: 10;
}
#action-area { position: relative; z-index: 20; }

.block{
    border-right:solid 2px rgb(5,162,234);
    border-left:solid 2px rgb(5,162,234);
    background:#F1F1F1;
    width:100%;
    padding:1px 15px;
    margin: 10px auto;
}
div.publicaff_memo{
    --memo-heading: "NOTICE FROM THE PUBLIC AFFAIRS DEPARTMENT";
    --memo-logo: url(https://scp-wiki.wikidot.com/local--files/drapricus-author-page/trans_logo) 50%;
    --memo-color: 240, 245, 255;
    background-size: 45%;
}
[[/module]]

