[[>]]
[[module rate]]
[[/>]]
[[module css]]
.emptybox{
    height:350px;
}
p{
position:relative;
display:block;
line-height:25px;
margin-block-start:0px;
margin-block-end:0px;
}
.butleft{
display: block;
transition-delay:114514s;
opacity:1;
height:33.6px;
width:45%;
background-color: rgb(196, 196, 196);
border-radius: 5px;
text-align: center;
color:black;
transition-duration:1s;
position:absolute;
top:0px;
left:0px;
}
.butleft:hover{
background-color: rgb(211, 211, 211);
color:black;
transition-delay:0s;
}
.butleft:active{
background-color: rgb(211, 211, 211);
color:black;
}
.butleft:visited{
color:black;
}
.butright{
display:block;
transition-delay:114514s;
opacity:1;
height:33.6px;
width:45%;
background-color: rgb(196, 196, 196);
border-radius: 5px;
text-align: center;
color:black;
transition-duration:1s;
position:absolute;
top:0px;
left:55%;
}
.butright:hover{
background-color: rgb(211, 211, 211);
color:black;
transition-delay:0s;
}
.butright:active{
background-color: rgb(211, 211, 211);
color:black;
transition-delay:0s;
}
.butright:visited{
color:black;
}

.turnstyle{
position:relative;
display:block;
height:0px;
overflow:hidden;
transition-delay:114514s;
transition-duration:1s;
width:100%;
opacity:0;
}
.qb{
    transition-delay:114514s;
    display:block;
    position:relative;
    height:0px;
    width:100%;
    opacity:0;
    transition-duration:1s;
        line-height:141%;
}
.qb p{
    position:relative;
    line-height:141%; 
}

