[[module css]]
div#container-wrap {
    background: url(http://themes.wdfiles.com/local--files/helloworld/background.png) top left repeat-x;
    background-size: 105px;
}
@media (max-width: 767px) {
#side-bar {
background-color: #000000;
}
}
#header {
    background-image: none;
}

#header #header-extra-div-1,
#header #header-extra-div-2,
#header #header-extra-div-3 {
    width: 6.250rem;
    height: 6.250rem;
    display: flex;
    position: absolute;
    left: 0.25rem;
    top: 2rem;
}

/* images */
#header #header-extra-div-1::before,
#header #header-extra-div-2::before,
#header #header-extra-div-3::before {
    content: "";
    width: 6.250rem;
    height: 6.250rem;
    position: absolute;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: contain;
    flex: 1;
}

#header #header-extra-div-1::before {
    z-index: 1;
    background-image: url();
    animation: cont-spin 15s linear infinite;
}

#header #header-extra-div-2::before {
    z-index: 3;
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/fragment%3Avrom-0/antimeme.png);
    animation: clock-spin cubic-bezier(.62,0,.52,1.3) 10s infinite;
}

#header #header-extra-div-3::before {
    z-index: 3;
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/fragment%3Avrom-0/antimeme.png);
    animation: clock-spin cubic-bezier(.62,0,.52,1.3) 10s infinite;
}

/* Animations */

@keyframes clock-spin
{
  0%, 15%
  {
    transform: rotate(0deg);
  }
  25%, 40%
  {
    transform: rotate(90deg);
  }
  50%, 65%
  {
    transform: rotate(180deg);
  }
  75%, 90%
  {
    transform: rotate(270deg);
  }
  100%
  {
    transform: rotate(360deg);
  }
}

@keyframes cont-spin {
    from {
        transform:rotate(0deg);
    }
    to {
        transform:rotate(360deg);
    }
}

/* Mobile */
@media (max-width: 767px)  {
    #header {
        background: none !important;
    }

    #header #header-extra-div-1, 
    #header #header-extra-div-2, 
    #header #header-extra-div-3 {
        width: 5rem !important;
        height: 5rem !important;
        left: -1rem !important;
        top: 3rem !important;
    }

    #header #header-extra-div-1::before, 
    #header #header-extra-div-2::before, 
    #header #header-extra-div-3::before {
        width: 5rem !important;
        height: 5rem !important;
    }
}
