[[include :scp-wiki:theme:penumbra]]

[[module CSS]]

.desktop-topper {display:initial;}
.mobile-topper {display:none;}

.desktop-topper a {color:#f00;}

/* Penumbra tweaks */

:root {
  --header-title: SCP Foundation";
  --lgurl: url("https://scp-wiki.wdfiles.com/local--files/theme:penumbra/tb.png");
  --accentColor:#f73636;
  --author-top-adjust:0.2rem;
}

body {
background-image: linear-gradient( to bottom, var(--darkerColor), var(--darkerColor) 90px, var(--darkerColor) 90px, var(--darkColor) 200px, var(--darkColor) 200px, #393634 100%);
}

.page-rate-widget-box {
  box-shadow: 0px 3px 0px 0px var(--accentColor);
  position: relative;
  z-index: 2;
}

#page-content .authorlink-wrapper {
  margin-top: 0.2rem;
}

.authorlink-wrapper > a {
  width:inherit !important;
  text-align:center !important;
  margin:auto;
  color: #fff;
}

.mobile-topper .scp-image-block img {
  border: 0;
  width: 100vw;
  max-width: none;
  left: calc(-50vw + 50%);
  position: relative;
}


.scp-image-block.block-fullwidth {
  width: 100vw !important;
  max-width: none !important;
  position: relative;
  left: calc(-50vw + 50%);
  margin-top:-4.5rem;
}

@media (max-width: 600px) {

  .desktop-topper {display:none;}
  .mobile-topper {display:block;}
}
[[/module]]

