[[module css]]
@import url(https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css);
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@200&display=swap');
@import url(https://scp-wiki.wdfiles.com/local--files/component%3Atheme/font-bauhaus.css);

@font-face {
    font-family: Inter;
    font-style: normal;
    font-weight: 100 900;
    font-display: swap;
    src: url('https://scpwiki.github.io/sigma/fonts/Inter-roman.var.woff2?v=3.19') format('woff2')
}

@font-face {
    font-family: Inter;
    font-style: italic;
    font-weight: 100 900;
    font-display: swap;
    src: url('https://scpwiki.github.io/sigma/fonts/Inter-italic.var.woff2?v=3.19') format('woff2')
}

@font-face {
    font-family: 'Sans Normalcy';
    font-style: normal;
    font-weight: 700;
    font-display: swap;
    src: url('https://scpwiki.github.io/sigma/fonts/Sans-Normalcy.woff2') format('woff2')
}

@font-face {
    font-family: 'Nanum Gothic';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: url('https://scpwiki.github.io/sigma/fonts/NanumGothic-Regular.woff2') format('woff2')
}

@font-face {
    font-family: 'Nanum Gothic';
    font-style: normal;
    font-weight: 700;
    font-display: swap;
    src: url('https://scpwiki.github.io/sigma/fonts/NanumGothic-Bold.woff2') format('woff2')
}

body {
    font-family: Inter, 'Noto Sans SC', BlinkMacSystemFont, 'heiti', 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
    font-feature-settings: 'case', 'ss01', 'ss04';
    font-size: 0.85em;
}

#header h1 a {
    font-family: 'Sans Normalcy', 'Noto Sans SC', 'Nanum Gothic', Arial, sans-serif;
}

@media (max-width:767px) {
    #top-bar .open-menu a {
        font-family: 'Nanum Gothic', 'Noto Sans SC', sans-serif;
    }

    body {
        font-size: 0.80em;
    }    
}

strong {
    font-weight: 800 !important;
}

tt {
    font-family: 'Andale Mono', 'Courier New', 'Noto Serif SC', Courier, 'songti', monospace;
    font-size: 100% !important;
}

.footnotes-footer {
display:none;
}
[[/module]]

