<style type="text/css">
@import url(http://d3g0gp89917ko0.cloudfront.net/v--50456838fc8c/common--theme/base/css/style.css);
@import url(http://www.scp-wiki.net/component:theme/code/1);

#times
{
    margin-top: 10px;
}
</style>
<script type="text/javascript">

// orginal "JS Magic" by Crayne 2014 //
// translate for SCP-JP by ikr_4185 2017 //

function showTimes(postBegin,postEnd,end)
{
    var times = document.getElementById('times');
    postBegin = new Date(postBegin*1000);
    postEnd = new Date(postEnd*1000);
    end = new Date(end*1000);
    postBegin = formatDate(postBegin);
    postEnd = formatDate(postEnd);
    end = formatDate(end);
    html = "投稿開始は" + postBegin + "から<br>";
    html += "投稿終了は" + postEnd + "まで<br>";
    html += "勝者は" + end + "に決定されます。";
    times.innerHTML = html;
}

function getWeekDay(num)
{
    var day;

    switch (num)
    {
     case 1:
     day = "月曜日";
     break;
     case 2:
     day = "火曜日";
     break;
     case 3:
     day = "水曜日";
     break;
     case 4:
     day = "木曜日";
     break;
     case 5:
     day = "金曜日";
     break;
     case 6:
     day = "土曜日";
     break;
     default:
     day = "日曜日";
    }

    return day;
}

function getMonthVerbose(num)
{
    var month;

    switch (num)
    {
     case 1:
     month = "2月";
     break;
     case 2:
     month = "3月";
     break;
     case 3:
     month = "4月";
     break;
     case 4:
     month = "5月";
     break;
     case 5:
     month = "6月";
     break;
     case 6:
     month = "7月";
     break;
     case 7:
     month = "8月";
     break;
     case 8:
     month = "9月";
     break;
     case 9:
     month = "10月";
     break;
     case 10:
     month = "11月";
     break;
     case 11:
     month = "12月";
     break;
     default:
     month = "1月";
    }

    return month;
}

function formatDate(d)
{
    var dateFormatted =getMonthVerbose(d.getMonth()) + "<strong>";
    var dayOfMonth = d.getDate();
    var suffix = "日";

    dateFormatted += dayOfMonth + suffix + "</strong>" +  getWeekDay(d.getDay()) + "の";

    var hours = d.getHours();
    var minutes = d.getMinutes();
    var timeString = "";

    if (hours > 12 || (hours == 12 && minutes > 0))
    {
     timeString = "午後" + (hours - 12) + ":" + minutes;
    }
    else
    {
     timeString = "午前" + hours + ":" + minutes;
    }

    dateFormatted += timeString;

    return dateFormatted;
}

</script>
</head>
<body onLoad="showTimes(1416632340,1417409940,1418014740);">

