content:
[[module css]]

.book {
    width: 30%;
    border: solid 1px #000;
    border-top: solid 4px #000;
    border-radius: 10px;
    overflow: hidden;
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 20px;
    text-align: center;
    background: #000000;
}

.book img {
    display: block;
    margin-left: auto;
    margin-right: auto;
}

.text {
    border-top: solid 10px #000;
    border-bottom: solid 1px #000;
    border-left: solid 8px #000;
    border-right: solid 8px #000;
    background: #FFFFFF;
    font-weight: bold;
}

.link {
    display: block;
    width: 49%;
    text-align: center;
    margin-top: -5px;
    margin-bottom: -5px;
}

.link a{
    color: #FFFFFF;
    font-weight: bold;
}
.epub {
    width: 100%;
    text-align: center;
}


[[/module]]

