[[row]]

[[cell style="padding: 10px; border: 1px solid silver; width: 25%;"]]
[[image https://scp-jp-storage.wdfiles.com/local--files/portal%3A3461191/%E3%82%AB%E3%82%A8%E3%83%8E%E3%83%AC%E3%83%A0%3D%E3%83%AA%E3%83%B4%E3%82%A3%E3%82%A8%E3%83%A9.jpg width="98%"]]
[[/cell]]

