#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 180%;
    text-shadow: none;
    text-decoration: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "Maxwellism";
color: #ffffff;
}

h1, #page-title {
    color: lightgreen;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '　神必完整，我们即神　';
color: #ffffff;

}

div#container-wrap{
