@import url('https://fonts.googleapis.com/css2?family=Dela+Gothic+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Do+Hyeon&display=swap');
@import url('https://fonts.googleapis.com/css?family=Black+Han+Sans:400');

#login-status a {
    color: #000;
}
#container {
    background: #eed;
}
div#container-wrap {
    background: unset;
}
#page-title {
    color: #d33;
    font-weight: bold;
    font-size: 250%;
}

#page-content {
    font-size: 1.2rem;
}
div#header {
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:3438721-60-rkrc/Electo_003.svg) 10px 10px no-repeat;
    background-size: 400px;
}
#header h1 {
    font-size: 300%;
    margin-left: 5px !important;
}
#header h1 a span, #header h2 span{
    display: none;
}
div#header h1 a {
    position: relative;
    width: 400px;
    margin: 10px auto 0 0;
}
div#side-bar div.side-block {
    background: transparent;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: #fff;
    border-color: #fff;
}
#side-bar .side-block {
    border: 2px solid transparent;
    box-shadow: none;
    background-color: transparent;
    color: #fff;
}

#side-bar .side-block[style="background-color: #a7dba2;"], #side-bar .side-block[style="background-color: #e5e5ff;"] {
    color: transparent!important;
}
#side-bar .side-block[style="background-color: #a7dba2;"], #side-bar .side-block[style="background-color: #e5e5ff;"], #side-bar .side-block[style="background-color: #fff0f0;"], #side-bar .side-block[style="background-color: #f0f0f0;"]{
    background-color: transparent!important;
}

#side-bar .heading {
    border-bottom: 2px solid fff;
    color: fff;
}
div.open-menu a {
    color: fff;
    border-color: transparent;
}
#top-bar a{
    color: #e01;
}
#search-top-box{
    display: none;
}

#main-content {
    margin: 0 0em 0 18em;
}
@media (max-width: 767px){
#main-content {
    margin: 0 5%;
}
}
.page-rate-widget-box .rate-points, .page-rate-widget-box .cancel, .rate-box-with-credit-button .creditButton{
    background-color: #d33 !important;
    border-color: #d33;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown{
    border-color: #d33;
}

.bolding{
    font-weight: bold;
}
.bolding p{
    line-height: 200%;
}
.dela{
    font-family: 'Dela Gothic One', 'Black Han Sans', cursive;
}
.hyeon{
    font-family: 'Do Hyeon', sans-serif;
    color: #d33;
    font-size: 250%;
    float: right;
    width: 50%;
    text-align: right;
    margin-left: 5%;
}
.hyeon p{
    margin-block: unset;
}

.slot-wrap-wrap{
    background: #ff0;
    padding: 5px 0;
}
.slot-wrap{
    margin: 5px 5px;
    border: solid 2px #000;
    display: flex;
    flex-wrap: wrap;
}
.slot{
    width: calc(50% - 14px);
    border: solid 2px #000;
    padding: 5px;
    display: flex;
    flex-wrap: wrap;
}

.slat{
    width: calc(100% - 14px);
    padding: 5px;
    display: flex;
    flex-wrap: wrap;
}
.fleft{
    float: left;
    width: 40%;
    padding: 5px;
}
.exp{
    padding-left: 5%;
    font-size: 80%;
    font-weight: bold;
}

h1{
    color: #d33;
}

a[href^="#u-"]{
    color: inherit;
    text-decoration: underline dashed 1px;
}

.biblio{
    text-align: right;
    margin-right: 1em;
    font-size: 50%;
}
