#u-modal-container {
    pointer-events: none;
    display: block;
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: 9999;
}

#u-modal-container .modal {
    display: block;
    position: absolute;
    pointer-events: none;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) scaleY(.1);
    width: 85%;
    max-width: 500px;
    max-height: 90vh;
    padding: 0 2em 2em 2em;
    z-index: 10000;
    opacity: 0;
    background: white;
    overflow-y: auto;
    transition: opacity .3s ease-in-out, transform .3s ease-in-out;
    border: double 4px;
    font-size: 1rem;
}

@media (max-width: 600px) {
  #u-modal-container .modal {
    padding: 0 1.5em 1em 1.5em;
  }
}

#u-modal-container .modal:target {
    opacity: 1;
    transform: translate(-50%, -50%) scaleY(1);
    pointer-events: auto;
}

#u-modal-closer {
    display: block;
    position: absolute;
    pointer-events: none;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0);
    z-index: 9999;
    transition: background .3s ease-in-out;
}

#u-modal-container .modal:target~#u-modal-closer {
    pointer-events: auto;
    background: rgba(0, 0, 0, 0.25);
}

#u-modal-container .content-block {
    margin: .25em auto;
    width: 90%;
    border: solid 1px rgba(0, 0, 0, 0.2);
    background: rgba(0, 0, 0, 0.01);
    padding: .25em .75em;
    line-height: 2;
}
