[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=PT+Mono&display=swap');
#header {background-image: url(https://scp-wiki.wdfiles.com/local--files/arbh-class-debrief-file/Pissresize);}
#header h1 a {
    display: block;
    margin: 0;
    padding: 90px 0 90px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi. 'Nanum Gothic'. Arial. sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "SCP Foundation";
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: -45px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Insect Hell';
    color: #fff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

sup {
  vertical-align: top;
  position: relative;
  top: -0.5em;
}

/* divs stolen from stormbreath uwu */

.parawatch blockquote {
    background:  #1a1a1a;
    border-width: 2px;
    border-style: solid;
    border-color: #262626;
    color: #f2f2f2;
    font-family: 'PT Mono', monospace;
}

.parawatch a {
     color: #ffff80;
}

/* DIV BLOCKS stolen from floppypheonix uwu */
.quote-block{
    border: 2px double #999;
    background-color: #f4f4f4;
    padding: .25em 1em;
    margin-bottom: 1em;
}
.report-box{
    border: 2px solid rgb(12, 12, 12);
    background-color: white;
    padding: .25em 1em;
    margin-bottom: 1em;
}
[[/module]]
