[[module CSS]]

#header h1 a:before {
    content: '扶 桑 紀';
}

#header h2:before {
    content: '日 落 餘 暉';
}

div#header-extra-div-1 {
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3282741-5-yh9l/Japanese_crest_Kiku_ni_Ichimonnji_2.svg);
}

div#page-content {
    height: 95%;
}

    .p444 ~ .list-pages-box{
        display: none !important;
    }

/* 诗歌 */

.poem {
height: auto;
writing-mode: vertical-rl;
writing-mode: tb-lr;
font-size:6vh;
margin: 1rem 0rem 0 1rem;
    float: right;
    letter-spacing:0.5rem;
    font-weight: 400;

}

#page-title,  .poem {
font-family: 'I.PenCrane-B','Ma Shan Zheng', '华文隶书', lisu,  'Shippori Mincho',  'Noto Serif SC' , '思源宋体 CN'; /*  润植家康熙字典美化体, nzgrKangxi,  */
}

#page-title{
text-align: center;
    font-size: 400%;
    letter-spacing: 0.5rem
}

#page-title, #breadcrumbs{
direction: rtl;
unicode-bidi:bidi-override;
}

#u-ratingboxhid, #breadcrumbs {   
 opacity:0.5;
  transition: all 0.5s ease-out;
}

#u-ratingboxhid:hover, #breadcrumbs:hover {  
 opacity:1;
}

#u-ratingboxhid,#u-ratingboxhid p {
    float: right;
    margin: 0;
    line-height: 0;
}

#u-ratingboxhid a[href="#u-skip"] {
display: none
}

@media   (min-width: 900px) {
#u-ratingboxhid a[href="#u-skip"] {
display: block
}
}

#html-body #page-content .page-rate-widget-box, div.creditRate {
  height: fit-content;
  width: auto;
}

@media   (max-width: 760px) {

#breadcrumbs, .pseudocrumbs {
  margin: 0em 0 1em;
}

.poem {
  font-size:200% !important;
margin: 0em 0em 0 0em !important;
    letter-spacing:0.3em
}

#page-title, #breadcrumbs{
direction:ltr;
}

#page-title{
direction:ltr;
    font-size: 200%;
}

#u-ratingboxhid, #breadcrumbs {   
 opacity:1;  
}
#page-content{
  right:auto;  
    position:initial;
}
}

@media (min-width:1200px) {   
 .poem p{
    background-image: linear-gradient(0deg, #333 100%, transparent);        
        -webkit-background-clip: text;
        color: transparent;
    white-space: nowrap;
    animation: w 4.5s forwards;
    overflow : hidden;
    background-clip: text;
    -webkit-text-fill-color:transparent;
    background-size: 100% 0%;
    background-repeat:  no-repeat;
}
}

@media (max-width:600px) {   
 div#main-content {
  height: 100%;
     padding: 0.5rem;
}

#html-body #page-content .page-rate-widget-box, div.creditRate {
  writing-mode: initial;
  height: 100%;
  width: auto;
  transform: none ;
  box-shadow: none;
}

div#page-content {
  min-height: auto;
  height: auto;
  padding:1rem;
  flex-direction: revert;
  box-sizing: border-box;
  display: block;
  writing-mode:   lr;

  width: 100%;
  margin: 0;
  position: relative;
}
.poem {
  writing-mode:   lr;
    float: left;
    font-size: 8vw !important;
    letter-spacing: 0.2em;
}
 .printuser, .mobile-top-bar ul, .mob-hid{
        display: none !important;
    }
}

 .p111  p{
    margin: 0
}

#u-skip:target ~ div p {
    background-size:100% 100% !important;
}

@keyframes w{
    0% {
                     background-size: 100% 0%;
    }
    100% {
                     background-size:100% 100%;
    }
}

@keyframes sss{
    0% {
                     background-size: 0% 100%;
    }
    100% {
                     background-size:100% 100%;
    }
}

:root {
  --fade-in-delay: 2s;
}
.p111 p:not(:nth-child(3))  { animation-delay: calc(5.5 * var(--fade-in-delay)); }
.p111 p:nth-child(1)  { animation-delay: calc(0 * var(--fade-in-delay)); }
.p111 p:nth-child(2)  { animation-delay: calc(1.5 * var(--fade-in-delay)); }
.p111 p:nth-child(3)  { animation-delay: calc(4 * var(--fade-in-delay)); }
.poem:not(.p111)  p{ animation-delay: calc(5.5 * var(--fade-in-delay)); }

