[[module css]]
@import url('https://fonts.googleapis.com/css?family=Caveat&display=swap');
@import url('https://fonts.googleapis.com/css?family=Didact+Gothic&display=swap');


div#container-wrap {
     background: url(http://smlt.wdfiles.com/local--files/jacrld%3A5k-2/light_bg2.png) top left repeat-x;
}

#side-bar .side-block,
#side-bar .media,
#side-bar .resources {
    background: #000000;
}

#side-bar .heading {
    color: #b01;
}

#side-bar div.side-block:nth-child(7) > div:nth-child(1) > div:nth-child(1) img {
    filter: none;
    -webkit-filter: none;
}

#side-bar .scpnet-interwiki-wrapper {
    width: 0em;
}

body {
    background-color: #000000;
    font-family: 'Didact Gothic', monospace;
    color: #ffffff;
}

#page-title {
  text-align: center;
}

#header h1 span {
    color: transparent;
    text-shadow: none;
}

#header h1 span::before {
     content: 'ERICS SITE';
     color: #FF0000;
     text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
     content: 'welcome to my page';
     color: #FF0000;
     text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

#header {
    background: url(http://scpdsandbox.wdfiles.com/local--files/collab%3Afamine-pastarasta1-collab/ericslogo.jpg) 0px 12px no-repeat;
}

ins, del, .hovertip {
    color: black;
}

[[/module]]



