    /* light grey, for visited links (and unvisited) */
    --bright-accent: 100, 5, 50;
    /* bright grey */
    --medium-accent: 100, 5, 50;
    /* medium/light grey - the "default" accent color */
    --dark-accent: 100, 5, 50;
    /* dark grey */
    --alt-accent: 100, 5, 50;
    /* light grey */
}

