[[module css]]
.danke{
padding: 5px 5px 5px 15px;
margin-bottom:10px;
width: 100%;
font-family: monospace;
font-size: 1.1em; }

.agent{
background-color:#002200;
border: 3px solid #55AA55;
color: #77CC77;
}

.site{
background-color:#222200;
border: 3px solid #AAAA55;
color: #DDDD77;
}
[[/module]]
[[module css]]
/*版头logo*/
#header {
    background-image: url(http://scp-wiki-cn.wdfiles.com/local--files/compromised-center/logo.png);
    background-repeat: no-repeat;
    background-size: 100px;
}
/*版头大字*/
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi, Simhei, Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #fff;
    letter-spacing: 0.9px;
}
#header h1 a::before {
    content: "Ground Zero";
    margin: 0 10px;
    font-size: 105%;
    font-family: Georgia, serif;
    color:#fff;
}
#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
    color:#7f00ff;
    text-shadow: 3px 3px 5px #fff;
}
/*版头小字*/
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
    color:trtransparent;
}

#header h2 span {
    display:block;
    margin:0 15px;
    padding: 15px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color:transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: "It's No Use,No Way Out";
    display: block;
    padding: 5px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #fff;
    text-shadow: 3px 3px 6px #fff;
    font-size: 125%;
    font-family: Georgia, serif;
}
/*版头背景*/
div#container-wrap {
    background-image: url(http://scpsandboxcn.wikidot.com/local--files/whlie-rock-2/%E8%87%AA%E5%88%B6css.png);
    background-repeat: repeat-x;
}
/*沙盒背景*/
body {
    font-size: 0.80em;
    color: #fff;
    background: #000;
}
/*文章标题*/
h1, #page-title {
    color: #fff;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
/*顶栏效果*/
#top-bar ul li a {
    border-left: none;
    border-right: none;
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}
/*点击顶栏按钮*/
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    background: #eee;
    color: #333;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}
/*搜索栏效果*/
#search-top-box-input {
    border: solid 1px #fff;
    border-radius: 0px;
    color: #fff;
    background-color: #000;
    box-shadow: inset 1px 1px 3px #fff;
}
/*点击搜索栏*/
#search-top-box-input:hover, #search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: #000;
    box-shadow: inset 1px 1px 3px #fff;
}
/*搜索按钮*/
#search-top-box-form input[type="submit"] {
    border: solid 1px #fff;
    border-radius: 0px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #ccc;
    background-color: #633;
    background: linear-gradient(#eee, #000);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
/*点击搜索按钮*/
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background-color: #966;
    background: linear-gradient(#000, #eee);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}
/*侧栏*/
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #fff;
    border-radius: 0px;
    box-shadow: 0 0px 20px #fff;
    margin-bottom: 15px;
    background: url(http://scpsandboxcn.wikidot.com/local--files/whlie-rock-2/%E7%99%BD%E5%BA%95.png);
}
/*侧栏字色*/
#side-bar div.menu-item a {
    font-weight: bold;
    color: #000;
}
/*侧栏小字*/
#side-bar .heading {
    color: #000;
    border-bottom: solid 1px #000;
    padding-left: 15px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
/*侧栏分割线*/
hr {
    margin: 1em 2em;
    padding: 0;
    height: 1px;
    border: none;
    color: #DDD;
    background-color: #000;
}
/*管理专用*/
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
    color: #000;
}
/*文章标题分割线*/
#page-title {
    border-color: #fff;
}
/*code栏*/
.code {
    border: 1px dashed #fff;
    background-color: #000;
    font-family: 'Andale Mono', 'Courier New', Courier, monospace;
    padding: 0 1em;
    margin: 0.4em 0;
    overflow: auto;
}
/*脚注锚点*/
a.footnoteref {
    display: inline-block;
    color: #fff;
}
/*超链接*/
a {
    color: #fff;
    text-decoration: none;
    background: transparent;
}
a:visited {
    color: #fff;
}
/*用户下拉表单*/
#login-status ul a {
    color: #000;
    background: transparent;
}
/*我的账户*/
#login-status a {
    background: transparent;
    color: #eee;
}
/*用户*/
#login-status {
    color: #fff;
    font-size: 90%;
}
/*提示窗*/
.hovertip {
    width: 20em;
    border: 2px solid #fff !important;
    background-color: #000;
    z-index: 100;
    color: #fff;
}
/*编辑器编辑按钮*/
div.buttons input, input.button, button, file, a.button {
    margin: 0 2px;
    padding: 0px 0.5em;
    text-decoration: none;
    background-color: #000;
    border: 2px solid #FFF;
    color: #FFF;
}
div.buttons input:hover, input.button:hover, button:hover, a.button:hover {
    background-color: #000;
    color: #FFF;
}
/*编辑器文字框*/
textarea {
    font-family: verdana,arial,helvetica,sans-serif;
    padding: 2px;
    font-size: 100%;
    border: 2px solid #FFF;
    background-color: #000;
    color: #FFF;
}
/*编辑器标题框*/
input.text {
    border: 2px solid #FFF;
    margin: 0 1px;
    padding: 1px 2px;
    background-color: #000;
    color: #FFF;
}
/*编辑器文字提示框*/
#lock-info {
    background-color: #000;
    border: 2px solid #fff;
    padding: 0.5em 1em;
    margin: 1em 0;
}
/*编辑器+-号*/
.change-textarea-size a {
    color: #fff;
    background-color: #000;
    border: 2px solid #fff;
    border-width: 0 1px 1px 1px;
    padding: 2px 4px;
    font-size: 120%;
    text-decoration: none;
}
/*编辑器底部提示小字*/
table.form td div.sub, form div.sub {
    font-size: 90%;
    color: #fff;
}
/*页底*/
#license-area {
    padding: 0.5em 0;
    color: #fff;
    text-align: center;
}
/*页脚*/
#footer {
    clear: both;
    font-size: 77%;
    background: #444;
    color: #fff;
    margin-top: 15px;
    padding: 3px 10px;
}
/*编辑按钮*/
.page-options-bottom a {
    margin: 2px 2px;
    font-size: 95%;
    padding: 0 8px;
    border: 2px solid #fff;
}
/*关闭按钮*/
a.action-area-close {
    float: right;
    margin: 2px 2px;
    font-size: 95%;
    padding: 0 0.7em;
    border: 2px solid #fff;
}
a.action-area-close:hover {
    background-color: #000;
}
/*tab栏按钮*/
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em {
    border-color: #FFF;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    border: solid #FFF;
    border-width: 0 1px;
    position: relative;
    text-decoration: none;
    color: #FFF !important;
    background: #000 !important;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-color: #FFF;
}
#page-content div, #page-content div table {
    max-width: 100%;
    background: #000;
}
.yui-navset {
    position: relative;
    border: solid 2px #fff;
}
/*图片插入*/
.scp-image-block img {
    border: 1px solid #fff;
}
.scp-image-block .scp-image-caption {
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    border: solid 1px #fff;
}
/*评分*/
.page-rate-widget-box .rate-points {
    background-color: #000 !important;
    border: solid 2px #fff;
        border-right-color: rgb(255, 255, 255);
        border-right-style: solid;
        border-right-width: 2px;
    border-right: 0;
    border-radius: 0px 0 0 0px;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #000;
    border-top: solid 2px #fff;
    border-bottom: solid 2px #fff;
    font-weight: bold;
}
.page-rate-widget-box .cancel {
    background-color: #000;
    border: solid 2px #fff;
    border-left: 0;
    border-radius: 0 0px 0px 0;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    background: transparent;
    color: #fff;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #fff;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: #000;
    color: #fff;
    text-decoration: none;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #000;
    color: #fff;
    text-decoration: none;
}
/*个人简介*/
.owindow {
    border: 2px solid #fff;
    text-align: left;
    position: absolute;
    background: #000;
}
.owindow .title {
    cursor: move;
    font-size: 120%;
    font-weight: bold;
    padding: 0.2em 1em;
    background-color: #000;
    height: 1%;
}
.owindow .button-bar a {
    margin: 0.2em 0.2em;
    padding: 0.1em 1em;
    text-decoration: none;
    background-color: #000;
    border: 2px solid #fff;
}
.owindow .button-bar a:hover {
    background-color: #000;
}
@media (max-width: 497px) {
#header {
    background-position: 0 3em;
}
#header h1 a::before {
    content: "Ground Zero";
    margin: 0 20px;
    font-size: 45%;
    font-family: Georgia, serif;
    color: #fff;
}
#header h2 span::before {
    content: "It's No Use,No Way Out";
    display: block;
    padding: 5px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #fff;
    text-shadow: 3px 3px 6px #fff;
    font-size: 80%;
    font-family: Georgia, serif;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 80%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0 0px;
    padding: 5px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
    position: relative;
    left: -30px;
    top: 0px;
}
}
[[/module]]
