}

#header::before {
    left: -12rem;
    filter: brightness(125%);
    opacity: 1;
}

#header h1 a::before {
    font-size: unset;
}

#header h2::before {
    color: #fe4;
}

#header h2 {
    top: 1em;
}

#header #login-status {
    left: -2vw;
}

@media (max-width: 767px) {
    #header::before {
        left: -7rem;
    }

    #header h1 {
        top: 0.5em;
        left: 0.5em;
    }
}

#page-title {
    display: none;
}

.bar p {
    margin: 0;
}

.bar {
    margin: 10px 0;
    padding: 3px 5px;
}

@keyframes animate-width {
    0%, 10% { width: var(--initial-width); }
    20%, 30% { width: var(--step-1-width); }
    40%, 50% { width: var(--step-2-width); }
    60%, 70% { width: var(--step-3-width); }
    80%, 90% { width: var(--step-4-width); }
    100% { width: var(--initial-width); }
}

@keyframes animate-content {
    0% { content: var(--step-1-content); }
    10% { content: var(--step-2-content); }
    30% { content: var(--step-3-content); }
    50% { content: var(--step-4-content); }
    70% { content: var(--step-5-content); }
    90% { content: var(--step-1-content); }
}

.dem {
    background: #4081C7;
    width: 80.8%;
    --initial-width: 80.8%;
    --step-1-width: 82.2%;
    --step-2-width: 80.6%;
    --step-3-width: 80.2%;
    --step-4-width: 80.4%;
    animation: animate-width 15s infinite;
}

.dem::after {
    content: "DEM: 40,4%";
    --step-1-content: "DEM: 40,4%";
    --step-2-content: "DEM: 41,1%";
    --step-3-content: "DEM: 40,3%";
    --step-4-content: "DEM: 40,1%";
    --step-5-content: "DEM: 40,2%";
    animation: animate-content 15s infinite;
}

.rep {
    background: #C74F40;
    width: 72.8%;
    --initial-width: 72.8%;
    --step-1-width: 72%;
    --step-2-width: 73%;
    --step-3-width: 73.4%;
    --step-4-width: 72.6%;
    animation: animate-width 15s infinite;
}

.rep::after {
    content: "REP: 36,4%";
    --step-1-content: "REP: 36,4%";
    --step-2-content: "REP: 36,0%";
    --step-3-content: "REP: 36,5%";
    --step-4-content: "REP: 36,7%";
    --step-5-content: "REP: 36,3%";
    animation: animate-content 15s infinite;
}

.non {
    background: #999999;
    width: 46.4%;
    --initial-width: 46.4%;
    --step-1-width: 45.8%;
    --step-2-width: 46.4%;
    --step-3-width: 46.4%;
    --step-4-width: 47%;
    animation: animate-width 15s infinite;
}

.non::after {
    content: "NONE: 23,2%";
    --step-1-content: "NONE: 23,2%";
    --step-2-content: "NONE: 22,9%";
    --step-3-content: "NONE: 23,2%";
    --step-4-content: "NONE: 23,2%";
    --step-5-content: "NONE: 23,5%";
    animation: animate-content 15s infinite;
}

.dot {
    animation: doting 2s infinite;
}

@keyframes doting {
    0%, 100% { opacity: 1; }
    50% { opacity: 0; }
}
