.div_shadow
{
    color: rgb(148, 148, 148);
    font-family: 'ZCOOL QingKe HuangYou', cursive;
    font-size: 19px;
    padding-top: 1em;
    padding-right: 2em;
    padding-left: 2em;
    padding-bottom: 2em;
    position: relative;

}
.div_shadow:after
{
content: " ";
background-image:repeating-linear-gradient(43deg,hsla(0,0%,90%,0),hsla(0,0%,100%,0) .25vh,rgba(180,180,180,0.1) .35vh,rgba(180,180,180,0.4) .4vh);
    background-size:100%;
    opacity: 1.0;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
}

.type1 {
    display: block;
    overflow: hidden;
    font-family: 'Long Cang', cursive;
    font-style: normal;
    font-size: 170%;
    color: rgb(57, 0, 0);

    background-attachment: scroll;
    background: url('http://scp-wiki.wdfiles.com/local--files/theme%3Amemoria/textback.jpg');

    background-clip: border-box;
    background-color: rgb(222, 222, 222);
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;

    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }

