:root {
    --header-title:"Harker";
    --header-subtitle:"An Extended Biography";
    --logo-image: url("https://scp-sandbox-3.wdfiles.com/local--files/queerlando-theme/quill_shadow.png");
}

#header h1 a:before {
    font-size: clamp(3rem, 24vw, 6rem);
}

#header h2 span::before {
    font-size: 1.3em;
    padding-top: 0.5em;
    margin-left: -0.5em;
    text-shadow: 0.125rem 0.125rem 0 rgb(79 79 79);
}

#page-title {
    font-family: "La Belle Aurore", cursive;
    margin-left: 0.5em;
    z-index: 2;
}

#page-title::after {
    top: calc(80%) !important;
    margin-left: -0.5em;
    z-index: -1;
}

.hrPad {
    background-image: url("https://scp-sandbox-3.wdfiles.com/local--files/letters-To-Harker/jasmine.png");
    background-size: initial;
    background-repeat: no-repeat;
    background-position: center;
    min-width: 100%;
    height: 2.5em;
    margin: 1em auto;
}

.storybook {
     background-image: url( https://scp-wiki.wikidot.com/local--files/scp-8166/paper-min%5B1%5D.png);
     padding:1em 2em;
     font-size: 105%;
     border: 2px solid black;
}

/*ANNABELLE*/
.handAnna {
    font-family: 'Long Cang', fantasy;
    font-size: 140%;
    line-height: 1.4em;
}

/*HARKER*/
.handHarker {
    font-family: "La Belle Aurore", cursive;
    font-size: 125%;
}

/*FORMAL*/
.hand3 {
    font-family: 'Qwitcher Grypen', cursive;
    font-size: 180%;
}

/*CHILD*/
.handChild {
    font-family: 'Schoolbell', cursive;
}

/* FADE OUT CODE BY JEZIXO */
.fade-out {
  -webkit-mask-image: linear-gradient(to bottom, black 0%, rgba(0,0,0,0.5) 60%, transparent 82%);
  mask-image: linear-gradient(to bottom, black 0%, rgba(0,0,0,0.5) 60%, transparent 82%);
    margin-bottom: -6em;
}

/* Radian Conditional Collapsibles Code */
body:has(.fontSwap .collapsible-block-unfolded[style="display: block;"]),
body:has(.fontSwap .collapsible-block-unfolded[style=""]) {
    .storybook {
    font-family: serif !important;
    }
}

#page-content a.collapsible-block-link {
    display: block;
    margin: 0 auto;
    width: fit-content;
font-weight: bold;
font-size: 125%;
}

