.blurry-box {
  position: relative;
  width: 100%;
  max-width: 1200px;
  margin-left: auto;
  background: rgba(0, 0, 0, 0);
  border-radius: 10px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
}

.blurry-box::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: inherit;
  filter: blur(10px);
  z-index: -1;
}

.blurry-box::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  backdrop-filter: blur(10px);
  mask-image: linear-gradient(
    to bottom,
    transparent 0%,
    rgba(0, 0, 0, 0.1) 15%,
    black 30%,
    black 70%,
    rgba(0, 0, 0, 0.1) 85%,
    transparent 100%
  );
  z-index: 0;
}

@media only screen and (min-width: 768px) {
  .blurry-box {
      width: 9999px;
      max-width: none !important;
      margin-left: 50%;
      transform: translateX(-50%);
  }
}

.quote {
  font-size: 38px;
  z-index: 1;
  text-shadow: 0 0 5px rgba(0, 0, 0, 255);
}

.quote2 {
  font-size: 28px;
  z-index: 1;
  text-shadow: 0 0 5px rgba(0, 0, 0, 255);
}

