content:
[[include :scpsandboxcn:no-saving-page]]
[[mOdUlE cSs]]
/* IPix中文像素字体↓ */
@import url(https://fastly.jsdelivr.net/gh/hoah2333/Fonts@main/Typeface-IPix.css);
/* 凤凰点阵体↓ */
@import url(https://fastly.jsdelivr.net/gh/hoah2333/Fonts@main/Typeface-VonwaonBitmap-12px.css);
/* 观致8px字体↓ */
@import url(https://fastly.jsdelivr.net/gh/hoah2333/Fonts@main/Typeface-GuanZhi-8px.css);

html, body {
    height: 100%;
    font-size: 96%;
}
:root {
    --m-extra-width: 767px;
    --pc-extra-width: ;
    --test-color: blue;
}
@media (max-width: 991px) {
    html, body {
        font-size: 76.8%;
    }
    :root {
        --m-extra-width: 767px;
        --pc-extra-width: ;
        --test-color: yellow;
    }
}
@media (max-width: 767px) {
    html, body {
        font-size: 60%;
    }
    :root {
        --m-extra-width: 100vw;
        --pc-extra-width: ;
        --test-color: green;
    }
}
body {
    overflow: hidden !important;
}
#u-html-content {
    width: auto;
    height: 100%;
    background-color: rgba(0, 0, 0, 1);
    padding: 0;
    margin: 0;
    position: fixed;
    z-index: 10;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}
#u-html-content p {
    display: block;
    width: 100%;
    height: 100%;
    padding: 0;
    margin: 0;
}
#u-html-content p iframe.html-block-iframe {
    width: 100%;
    height: 100% !important;
}

/* 字体↓ */
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.rate-points, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.rate-points, #u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.rateup, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.rateup, #u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.ratedown, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.ratedown, #u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.cancel, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.cancel {
    width: 2rem;
    height: 2rem;
    display: inline-block;
    font-size: 1.5rem;
    line-height: 2rem;
    font-weight: normal;
    letter-spacing: 0.25rem;
    vertical-align: middle;
    animation: fontMixRateAnim 0.2s linear infinite;
    margin-top: 0.1rem;
    --ls_val: 0.23rem;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.rate-points, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.rate-points {
    width: 9rem;
    --ls_val: 0.3rem;
}
#u-credit-view:target div.modalcontainer div.modalbox div h2 span, #u-credit-view:target div.modalcontainer div.modalbox div > p:nth-of-type(2) {
    display: inline-block;
    height: 2rem;
    line-height: 2rem;
    letter-spacing: 0.25rem;
    vertical-align: middle;
    animation: fontMixAnim 0.3s linear infinite;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.creditButton p {
    width: 2.1rem;
    height: 2.1rem;
    display: inline-block;
    line-height: 2rem;
    font-weight: normal;
    letter-spacing: 0.25rem;
    vertical-align: middle;
    animation: fontMixRateAnim 0.2s linear infinite;
    margin-top: 0rem;
    --ls_val: 0.23rem;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.rate-points {
    padding-left: 1rem;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.creditButton p a {
    font-size: 2rem;
    padding-left: 0.5rem;
}
@keyframes fontMixRateAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    49% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    50% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: var(--ls_val); }
    99% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: var(--ls_val); }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
}
@keyframes fontMixAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; }
    33% { font-family: 'VonwaonBitmap 12px', sans-serif; }
    34% { font-family: 'GuanZhi 8px', sans-serif; }
    66% { font-family: 'GuanZhi 8px', sans-serif; }
    67% { font-family: IPix, sans-serif; }
    99% { font-family: IPix, sans-serif;  }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; }
}
/* 字体↑ */

/* 评分模组↓ */
#u-rate-content {
    opacity: 0;
    margin-top: -4rem;
    position: fixed;
    z-index: 12;
    top: 0;
    right: -0.6rem;
    animation: rateOpacHideAnim 30s linear;
    transition: opacity 1s ease, margin-top 1s ease;
}
@keyframes rateOpacHideAnim {
    0% { margin-top: 0; opacity: 1; }
    96.6% { margin-top: 0; opacity: 1; }
    100% { margin-top: -4rem; opacity: 0; }
}
#u-onin {
    width: 20rem;
    height: 3rem;
    position: fixed;
    z-index: 11;
    top: 0;
    right: -0.6rem;
}
#u-onin:hover + #u-rate-content, #u-rate-content:hover {
    opacity: 1;
    margin-top: 0;
    transition: opacity 1s ease, margin-top 1s ease;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button {
    background-color: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none;
    padding: 0;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box {
    color: #F00;
    height: 2rem;
    border-radius: 0;
    box-shadow: none;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box span.rate-points, #u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.creditButton p a, #u-credit-view:target div.modalcontainer div.modalbox div.page-rate-widget-box span.rate-points {
    color: #F00;
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.page-rate-widget-box {
    margin-right: 0.15rem;
}
.page-rate-widget-box .rate-points, .page-rate-widget-box .rateup, .page-rate-widget-box .ratedown, .page-rate-widget-box .cancel {
    background-color: rgba(255, 0, 0, 0.3);
    border-color: rgba(255, 0, 0, 0);
    border-width: 0.2rem;
    border-radius: 0;
    padding: 0.2rem;
}
.page-rate-widget-box .rate-points {
    background: rgba(255, 0, 0, 0.3) !important;
    padding-left: 0.5rem;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a, .page-rate-widget-box .cancel a {
    color: #F00;
}
.page-rate-widget-box span.cancel a[title="取消我的投票"] {
    border-radius: 0;
}
.page-rate-widget-box span.rateup:hover, .page-rate-widget-box span.ratedown:hover, .page-rate-widget-box span.cancel:hover {
    background-color: #F00;
}
.page-rate-widget-box span.rateup:hover a, .page-rate-widget-box span.ratedown:hover a, .page-rate-widget-box span.cancel:hover a {
    background-color: #F00;
    color: rgba(255, 0, 0, 0.3);
}
#u-rate-content div.creditRate div.rateBox div.rate-box-with-credit-button div.creditButton {
    border: solid 0.3rem rgba(255, 0, 0, 0);
    background-color: rgba(255, 0, 0, 0.3);
    padding: 0.3em 0.1rem;
    padding-left: 0.5rem;
}
.rateBox .rate-box-with-credit-button .creditButton p a {
    border: none;
}
.rateBox .rate-box-with-credit-button .page-rate-widget-box span.cancel {
    border-right: solid 0.2rem rgba(255, 0, 0, 0);
}
#u-credit-view:target div.modalcontainer div.modalbox {
    color: #F00;
    font-size: 1.5rem;
    background: rgba(50, 0, 0, 0.3);
}
/* 评分模组↑ */
[[/mOdUlE]]
[[div id="onin"]]
[[/div]]
[[div id="rate-content"]]
[[include :scp-wiki-cn:credit:start]]
