    padding: 0;
    margin: 0;
}
#nps button#load_valbox {
    font-size: 1.2rem;
}
#html_main_content {
    width: 100%;
    height: 100%;
    font-size: 1.5rem;
    background-color: rgba(255, 0, 0, 0.1);
    overflow-x: hidden;
    overflow-y: hidden;
    animation: bgHmcAnim 0.3s linear infinite;
}
@keyframes bgHmcAnim {
    0% { background-color: rgba(255, 0, 0, 0.1); }
    49% { background-color: rgba(255, 0, 0, 0.1); }
    50% { background-color: rgba(255, 0, 0, 0.11); }
    99% { background-color: rgba(255, 0, 0, 0.11); }
    100% { background-color: rgba(255, 0, 0, 0.1); }
}
/* 主体↑ */

/* root值↓ */
html, body {
    font-size: 125%;/* 16px→20px，1rem = 20px */
}
:root {
    --m-extra-width: 767px;
    --pc-extra-width: ;
    --test-color: blue;
}
@media (max-width: 991px) {
    html, body {
        font-size: 100%;/* 16px→16px，1rem = 16px */
    }
    :root {
        --m-extra-width: 767px;
        --pc-extra-width: ;
        --test-color: yellow;
    }
}
@media (max-width: 767px) {
    html, body {
        font-size: 78.125%;/* 16px→12.5px，1rem = 12.5px */
    }
    :root {
        --m-extra-width: 100vw;
        --pc-extra-width: ;
        --test-color: green;
    }
}
/* root值↑ */

/* 字体↓ */
.fontNsa {
    font-family: 'Noto Sans SC', sans-serif;
}
.fontNse {
    font-family: 'Noto Serif SC', sans-serif;
}
.fontIPix {
    font-family: IPix, sans-serif;
}
.fontZcool {
    font-family: 'ZCOOL QingKe HuangYou', sans-serif;
}
.fontVb12 {
    font-family: 'VonwaonBitmap 12px', sans-serif;
}
.fontVb16 {
    font-family: 'VonwaonBitmap 16px', sans-serif;
}
.fontGz8 {
    font-family: 'GuanZhi 8px', sans-serif;
}
.fontMix {
    display: inline-block;
    height: 2rem;
    line-height: 2rem;
    letter-spacing: 0.25rem;
    vertical-align: middle;
    animation: fontMixAnim 0.3s linear infinite;
}
@keyframes fontMixAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; }
    33% { font-family: 'VonwaonBitmap 12px', sans-serif; }
    34% { font-family: 'GuanZhi 8px', sans-serif; }
    66% { font-family: 'GuanZhi 8px', sans-serif; }
    67% { font-family: IPix, sans-serif; }
    99% { font-family: IPix, sans-serif;  }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; }
}
.fontMixEng {
    display: inline-block;
    height: 1.8rem;
    line-height: 2rem;
    vertical-align: middle;
    animation: fontMixEngAnim 0.2s linear infinite;
}
@keyframes fontMixEngAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    49% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    50% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: 0.4rem; }
    99% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: 0.4rem; }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
}
.fontMixNum {
    display: inline-block;
    height: 1.8rem;
    line-height: 1.8rem;
    vertical-align: middle;
    animation: fontMixNumAnim 0.2s linear infinite;
}
@keyframes fontMixNumAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    49% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    50% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: 0.2rem; }
    99% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: 0.2rem; }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
}
.fontMixDot {
    display: inline-block;
    height: 1.8rem;
    line-height: 2rem;
    vertical-align: middle;
    animation: fontMixDotAnim 0.2s linear infinite;
}
@keyframes fontMixDotAnim {
    0% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    49% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
    50% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: -1rem; }
    99% { font-family: 'GuanZhi 8px', sans-serif; letter-spacing: -1rem; }
    100% { font-family: 'VonwaonBitmap 12px', sans-serif; letter-spacing: 0; }
}
/* 字体↑ */

/* 手绘基金会↓ */
.logoScp {
    width: 10rem;
    height: 10rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    animation: logoAnim 0.3s linear infinite;
    --logoUrl_1: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_scp红logo_1");
    --logoUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_scp红logo_2");
    --logoUrl_3: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_scp红logo_3");
}
@keyframes logoAnim {
    0% { background-image: var(--logoUrl_1); }
    32% { background-image: var(--logoUrl_1); }
    33% { background-image: var(--logoUrl_2); }
    65% { background-image: var(--logoUrl_2); }
    66% { background-image: var(--logoUrl_3); }
    99% { background-image: var(--logoUrl_3); }
    100% { background-image: var(--logoUrl_1); }
}
/* 手绘基金会↑ */

/* 手绘缺失↓ */
.logoDeletion {
    width: 11rem;
    height: 11rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    margin-top: -0.5rem;
    animation: imgAnim 0.2s linear infinite;
    --imgUrl_1: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_缺失logo_1");
    --imgUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_缺失logo_2");
}
/* 手绘缺失↑ */

/* 手绘中心↓ */
