    --logoUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_失温症logo_2");
    --logoUrl_3: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_失温症logo_3");
}
/* 手绘中心↑ */

/* 手绘猫↓ */
.logoCat {
    width: 11rem;
    height: 11rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    margin-top: -0.5rem;
    animation: logoAnim 0.3s linear infinite;
    --logoUrl_1: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_猫logo_1");
    --logoUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_猫logo_2");
    --logoUrl_3: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_猫logo_3");
}
/* 手绘猫↑ */

/* 手绘墓碑↓ */
.imgGrave {
    width: 15rem;
    height: 15rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    animation: imgAnim 0.2s linear infinite;
