}
/* 手绘花↑ */

/* 手绘铲子↓ */
.imgShovel {
    width: 8rem;
    height: 8rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    animation: imgAnim 0.2s linear infinite;
    --imgUrl_1: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_铲_1");
    --imgUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_铲_2");
}
/* 手绘铲子↑ */

/* 手绘棺木↓ */
.imgCoffinA {
    width: 15rem;
    height: 15rem;
    background-size: 100% 100%;
    background-position: 50%;
    background-repeat: no-repeat;
    margin: 0 auto;
    animation: imgAnim 0.2s linear infinite;
    --imgUrl_1: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_棺A_1");
    --imgUrl_2: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_棺A_2");
}
.imgCoffinB {
    width: 15rem;
    height: 15rem;
