/* 红色之雨↓ */
.redRain::before, .redRain::after {
    content: "";
    display: block;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
}
.redRain::before {
    background-image: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_雨A");
    animation: redRainAnim 0.8s linear infinite;
}
.redRain::after {
    background-image: url("https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/手绘_雨B");
    animation: redRainAnim 0.5s linear infinite;
}
@keyframes redRainAnim {
    0% { background-position: 0% 0%; }
    100% { background-position: 0% 500px; }
}
