/* 页000↓ */
#A_000 {
    width: 100%;
    min-height: 100%;
    position: relative;
    top: 0;
    left: 0;
}
#A_000 div {
    z-index: -1;
}
#A_000 > div {
    position: absolute;
}
#A_000_Scp, #A_000_Hypo, #A_000_Cat {
    width: 12rem;
    top: calc(50% - 10rem);
    left: calc(50% - 6rem);
}
#A_000_Deletion {
    width: 15rem;
    top: calc(50% - 10rem);
    left: calc(50% - 7.5rem);
}
#A_000_Flow {
    width: 90%;
    top: 30%;
    left: calc(5% + 1rem);
}
#A_000_Flow div span {
    font-size: calc(1rem + (var(--m-extra-width) / 36));
    letter-spacing: 2rem;
}
