/* logo动画↓ */
function logoAnimFunc(name, num, time) {
    if (num==1) $("#A_000_" + name).css("z-index", "1");
    if (num==0) $("#A_000_" + name).css("z-index", "-1");
    $("#A_000_" + name).animate({
        opacity: num
    }, time, "swing");
    $("#A_000_" + name + " .logo" + name).animate({
        width: '-=1rem',
        height: '-=1rem',
        marginTop: '+=0.5rem'
    }, time, "swing");
}
/* logo动画↑ */

/* 显隐动画↓ */
function opacityFunc(name, num, time) {
    if (num==1) $(name).css("z-index", "1");
    if (num==0) $(name).css("z-index", "-1");
    $(name).animate({
        opacity: num
    }, time, "swing");
}
