/* 拖动动画↓ */
function touchAnim(target, scope, callback) {
    cd = 1;
    var oldX;
    var oldY;
    var startX;
    var startY;
    var endX;
    var endY;
    var divLeft;
    var divRight;
    var divTop;
    var divBottom;
    if (device.desktop()) {
        $(target).on("mousedown", function(e) {
            oldX = e.clientX,
            oldY = e.clientY;
            startX = oldX;
            startY = oldY;
            endX = oldX;
            endY = oldY;
            divLeft = $(scope).offset().left;
            divRight = $(scope).offset().left +$(scope).width();
            divTop = $(scope).offset().top;
            divBottom = $(scope).offset().top +$(scope).height();
            $(document).on("mousemove", function(e) {
                endX = e.clientX,
                endY = e.clientY;
                $(target).css('marginTop', '+=' + (endY - startY));
                $(target).css('marginLeft', '+=' + (endX - startX));
                startX = endX;
                startY = endY;
            });
            $(document).on("mouseup", function() {
                $(target).css("pointer-events", "none");
                $(document).off("mousemove");
                if (endX>=divLeft&&endX<=divRight&&endY>=divTop&&endY<=divBottom) {
                    if (target=="#A_000_Shovel"&&item_Hole<2) item_Hole += 1;
                }
                callback();
                $(target).animate({
                    marginTop: '0',
                    marginLeft: '0'
                }, 500, function() {
                    cd = 0;
                    $(this).css("pointer-events", "");
                    endX = oldX;
                    endY = oldY;
                    $(document).off("mouseup");
                });
            });
        });
    } else {
        $(target).on("touchstart", function(e) {
            oldX = e.originalEvent.changedTouches[0].clientX,
            oldY = e.originalEvent.changedTouches[0].clientY;
            startX = oldX;
            startY = oldY;
            endX = oldX;
            endY = oldY;
            divLeft = $(scope).offset().left;
            divRight = $(scope).offset().left +$(scope).width();
            divTop = $(scope).offset().top;
            divBottom = $(scope).offset().top +$(scope).height();
        });
        $(target).on("touchmove", function(e) {
            endX = e.originalEvent.changedTouches[0].clientX,
            endY = e.originalEvent.changedTouches[0].clientY;
            $(this).css('marginTop', '+=' + (endY - startY));
            $(this).css('marginLeft', '+=' + (endX - startX));
            startX = endX;
            startY = endY;
        });
        $(target).on("touchend", function() {
            $(this).css("pointer-events", "none");
            if (endX>=divLeft&&endX<=divRight&&endY>=divTop&&endY<=divBottom) {
                if (target=="#A_000_Shovel"&&item_Hole<2) item_Hole += 1;
            }
            callback();
            $(this).animate({
                marginTop: '0',
                marginLeft: '0'
            }, 500, function() {
                cd = 0;
                $(this).css("pointer-events", "");
                endX = oldX;
                endY = oldY;
            });
        });
    }
}
