[[module CSS]]

div#container-wrap {
    background: url(http://scp-sandbox-3.wdfiles.com/local--files/collab%3Aar-theme/ar_bg.png) top left repeat-x;
}
#header h1 a {
    background: transparent;
    font-family: 'Roboto Mono',微软雅黑,sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: -0.05em;
    font-weight: 400;
    /* hide real header */
    line-height: 0px;
    max-height: 0px;
    color: transparent;
}
#header h1 a::before {
    content: "Cradle of Starseeker";
    color: #fff;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "树叶间有沙沙的响";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}
div#container-wrap {
    background: linear-gradient(to top, #9e9a75, #41533b, #604439);
    background-size: 100% 165px;
    background-repeat: no-repeat;
}
div#side-bar div.side-block {
    background: rgb(165, 140, 63);
    border: #604439;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgba(144, 55, 11, 0.93);
    border-color: rgba(144, 55, 11, 0.93);
}

div.open-menu a {
    color: rgba(144, 55, 11, 0.93)!important;
    border-color: rgba(144, 55, 11, 0.93)!important;
}
body {
    background: rgba(255, 228, 146, 0.7);
    color: rgb(127, 96, 0);
}
a,a:visited {
    color: rgb(127, 96, 0);
}
h1,h2,h3,h4,h5,h6 {
    color: rgb(127, 96, 0);
}
#page-title {
color:rgb(96, 68, 57)
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgb(127, 96, 0)!important;
    border: solid rgb(127, 96, 0) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgb(127, 96, 0)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: rgb(127, 96, 0)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}
div.yui-navset div.yui-content {
    background: rgba(255, 228, 146, 0.7);
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    color: rgb(127, 96, 0);
    border: rgb(127, 96, 0);
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(127, 96, 0);
    border: rgb(127, 96, 0);
}
