[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Mystery+Quest');
.lie{
     margin: 1px;

     background-color: hsla(207, 75%, 50%, .3);

     word-break: unset;
     font-family: var(--mono-font);
}
:root { --swatch-primary: 187, 0, 17; }
.story {
  clear: both; border: 3px black solid; padding: calc(20px - 1em) 0 20px 0;
}
.story p, .choices {
  margin-left: 20px; margin-right: 20px;
}

.choices {
  clear: both;
}
.choices p {
  clear: both; margin: 0; margin-top: 0.5rem;
}
span, a {
  word-break: normal !important;
}

/* Improved arrow CSS by EstrellaYoshte */
/* thank you estrella! */
.choices a {
    width: max-content;
    max-width: 70%;
    background-color: transparent;
    padding: 3px 6px;
    position: relative;
    display: block;
    z-index: 0;
    --border-thickness: 0.0625rem;
    clip-path: polygon(0 -5%, 130% -5%, 130% 105%, 0 105%);
    border-left: var(--border-thickness) solid rgb(var(--swatch-primary));
}
.choices a::before, .choices a::after {
    content: "";
    position: absolute;
    height: 50%;
    width: 116%;
    display: block;
    z-index: -1;
    left: 0;
    box-sizing: border-box;
    border: solid var(--border-thickness) rgb(var(--swatch-primary));
    border-left-width: 0;
    border-right-width: calc(var(--border-thickness)*1.35);
}
.choices a::before {
    top: 0;
    transform: skew(35deg);
    border-bottom-width: 0;
}
.choices a::after {
    bottom: 0;
    transform: skew(-35deg);
    border-top-width: 0;
}
.no {
    color: transparent;
}
.no::selection {
    color: #FF6666;
}
div.jgterminal{
    border: 1px solid pink;
    border: solid 3px #ff94b4;
    border-radius: 16px;
    background-color: #ffb0d0;
/* black shadow at the top of the jgterminal */
    background-image:
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #ffedf5);
    background-position: center;
    display: block;
/* shadow around the jgterminal */
    box-shadow: inset 0 0 10em 1em rgba(252,217,231,0.96);
/* prevents scanline from forming a scrollwheel */
    overflow:hidden;
}
div.jgterminal blockquote {
    background-color: #fff2f7;
    border: double 3px #ff94b4
}
div.jgscanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
/* Safari 4.0 - 8.0 */
    -webkit-animation: scan 12s linear 0s infinite; /* You may need to change this. If the bar goes by too quickly, increase 5s. */
    animation: scan 12s linear 0s infinite; /* Same as above */
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.jgtext{
    color: rgba(148,0,108,1);
    padding-left: 2em;
    padding-right: 2em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
    }

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* You may need to change this depending on the length of your terminal. If the scanline stops halfway, increase the second number.*/
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* Same as above.*/
}

div.jgtext a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #ff6392;
    padding: 1px;
}
div.jgtext a:hover::before{
content: "> ";
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}

.fancyborder {
    box-sizing: border-box;
    padding: 2vw;
    border: 2vw solid rgba(0, 0, 0, 0.5);
    border-image: url("https://wanderers-library.wikidot.com/local--files/component:dustjacket-theme/wl_border.png") 600 round;
    border-image-width: 6;
}
