<head>
<style>
@import url('https://fonts.googleapis.com/css2?family=Inter&display=swap');
body {
  font-family: Inter, Verdana, sans-serif;
  font-size: 14px;
}
p {
  display: flex;
  align-items: center;
}
#timer {
  background: #f4f4f4;
  border: 1px dashed #999;
  min-width: 6ch;
  text-align: center;
  margin-right: 1ch;
  padding: 3px 0;
}
</style>
<script>

const descriptions = [
  "SCP-3211's file can only be read once. After six minutes, it permanently becomes an entirely different file.",
  "SCP-3211's documentation changes after six minutes, and it can only be read once.",
]
const finalDescription = "SCP-3211 is nothing."

const timerSeconds = 360
const timerKey = "format-screw-list-3211-timer"

function countdown(time) {
  const minutes = Math.floor(time / 60)
  const seconds = time - minutes * 60
  document.getElementById("timer").textContent = (
    minutes.toString().padStart(2, "0") + ":" + seconds.toString().padStart(2, "0")
  )
  if (time > 0) {
    setTimeout(countdown, 1000, time - 1)
  } else {
    document.getElementById("desc").innerText = finalDescription
  }
}

function forgetTimer() {
  localStorage.removeItem(timerKey)
  console.log("3211: forgot timer")
}

// Once everything has loaded...
addEventListener("load", () => {
  // Set initial description
  const randomDesc = descriptions[Math.floor(Math.random() * descriptions.length)]
  document.getElementById("desc").innerText = randomDesc

  // Recall or set the timer expiry time
  let timestamp
  const now = Date.now()
  if (localStorage.getItem(timerKey)) {
    timestamp = parseInt(localStorage.getItem(timerKey))
    console.log("3211: timestamp is " + new Date(timestamp) + " from storage")
  } else {
    timestamp = parseInt(now + (timerSeconds * 1000))
    localStorage.setItem(timerKey, timestamp)
    console.log("3211: timestamp is " + new Date(timestamp) + ", fresh")
  }

  // Start the timer
  countdown(Math.max(0, Math.floor((timestamp - now) / 1000)))

  // Click the timer to forget the timestamp
  document.getElementById("timer").addEventListener("click", forgetTimer)
})

</script>
</head>

<body>
  <p><span id="timer">00:00</span><span id="desc"></span></p>
