[[html]]
<img id="pic" width="640px" alt="" class="image" />
<script type="text/javascript">
var firstUrl = "http://jinns-little-greenhouse.wdfiles.com/local--files/2521-xxx/Jinn2521XXX-6.png";
var secondUrl = "http://jinns-little-greenhouse.wdfiles.com/local--files/2521-xxx/Jinn2521XXX-7.png";

new Image().src = firstUrl;

new Image().src = secondUrl;

window.onload = function(){

var org = window.document.getElementById('pic');
org.src = firstUrl;
org.onmouseover = function(ev){
this.src = secondUrl;
}
org.onmouseout = function(ev){
this.src = firstUrl;
}

 org.addEventListener('touchstart',org.onmouseover,false);
 org.addEventListener('touchend',org.onmouseout,false); 
}
</script>
[[/html]]
