[[module CSS]]


/*
    None CSS with Left Beef
    [2019 Wikidot Theme]
    Created by stormbreath.
*/

/* LINKS */
a {
    color: #0645AD;
}

a.newpage {
    color: #CC2200;
}

 a:visited {
     color: #0B0080;
}

#side-bar a,
#interwiki a {
     color: black
}

#side-bar a:visited,
#interwiki a:visited {
     color: black;
}

/* REMAKE THE HEADER */
h1, #page-title {
    color: black;
}

#page-title {
    border-color: black;
}

div#container-wrap {
    background: none;
}

div#extra-div-1 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: black;
}

#header {
    background-image: url('http://scp-wiki.wikidot.com/local--files/scp-8009/Illyriel.png');
    background-size: 8rem;
}

#header h1 a::before {
    content: "天体档案馆";
    color: black;
}

#header h2 span::before {
    content: "伊利瑞尔之盟约";
    color: black;
}

@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }
}

#header h1 a {
    color: transparent;
    text-shadow: none;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

/* SIDE-BAR */
#side-bar .side-block,
#interwiki .side-block {
    background-color: white !important;
    border-color: transparent;
    border-radius: 0px;
    box-shadow: none;
}

#side-bar .heading,
#interwiki .heading {
    color: black;
    border-bottom: solid 1px black;
}

@media (max-width: 767px) {   
    #side-bar {
         background-color: white;
    }

    #side-bar:target .close-menu {
         background: none;
    }
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px black;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: black;
}

#interwiki .menu-item img{
    filter: grayscale(100%) saturate(0%);
}

/* TOP-BAR */
#top-bar ul li ul {
    border: solid 1px black;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid white;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: white;
    color: black;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: black;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: black;
    color: white; /* top bar hover background color */
}

/* RATING MODULE */
div.page-rate-widget-box .rate-points {
    background-color: black;
    border: solid 1px black;
    text-transform: capitalize;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: black;
    border-top: solid 1px black;
    border-bottom: solid 1px black;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: white;
    color: black;
}

.page-rate-widget-box .cancel {
    background-color: black;
    border: solid 1px black;
}

.page-rate-widget-box .cancel a {
    color: white;
}

.page-rate-widget-box .cancel a:hover {
    background: white;
    color: black;
}

#page-content .rate-box-with-credit-button {
    background-color: black;
    border: 1px solid black;
    border-radius: 5px !important;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}

.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
     color: black;
     background-color: white;
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(102, 102, 102, .5);
}

.close-credits,
.credit-back {
    filter: hue-rotate(260deg);
}

/* PAGE ELEMENTS */
blockquote,
div.blockquote,
#toc,
.code {
     background-color: white;
}

.scp-image-block {
    border: solid 1px black;
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    border-top: solid 1px black !important;
    color: black;
}

/* RECOLOR THE SEARCH BAR */
#search-top-box-input {
    border-color: black;
    color: black !important;
    background-color: white;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: white;
    border-color: black;
}

#search-top-box-form input[type=submit] {
    border: solid 1px black;
    color: black;
    background-color: white;
    background: white;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px black;
    background-color: white;
    background: white;
    color: black;
}

/* LOGIN STUFF */
#login-status {
    color: black;
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}

#login-status a {
    color: black;
}

#login-status ul a {
    color: black;
}

#account-topbutton {
     background: #ccc;
     color: black;
}

/* HOVER THINGIES */
.hovertip {
     background: white !important;
}

/* FOOTER  */
#footer {
    background: black;
    color: white;
}

/* TABS */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent; /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: white; /* tab background */
    border-color: white;
    color: black;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
    background: white;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    border-color: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: white; /* selected tab background */
    color: black;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px 0.5px rgba(0,0,0,0.5);
    background: white;
}

.yui-navset .yui-content {
    background: transparent; /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: transparent; /* content border */
    border-top-color: transparent; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

body {
    background: rgb(227, 234, 240);

