[[module css]]
@import url(http://scp-jp.wikidot.com/local--code/scp-8888/2);

:root {
  --backdrop: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6370408-474-igl9/wowee.png);
}

#page-content .introonly {
  display: block !important;
}
[[/module]]


