[[include :scp-wiki:theme:black-highlighter-theme]]
[[module css]]
:root {
    --logo-image: url(https://i.imgur.com/p36pbPn.png);
    --header-title: "IGNOTA'S MYCOTHERAPY";
    --header-subtitle: "YOU CANNOT KILL ME IN A WAY THAT  MATTERS";
    --body-font: Courier,Courier,Courier,"Courier New","Courier New","Courier",Courier,serif;
    --UI-font: Courier,Courier,Courier,Courier,"Courier New",Courier,Courier,Courier,Courier,"Courier New",serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";
    --header-font: "Courier New","Courier New","Courier New","Courier New","Courier New","Courier New","Courier",Courier,serif;
    --mono-font: Courier,Courier,Courier,monospace;
    --base-font-size: var(--max-font-size);
}
:root {
    --sidebar-width-on-desktop: min(calc(var(--base-font-size)*0.93333*19),calc(100vw - var(--body-width-on-desktop)));
    --accentColor: #ff00ca;
    --white-monochrome: 0,0,0;
    --pale-gray-monochrome: 107, 83, 115;
    --light-pale-gray-monochrome: 107, 83, 115;
    --very-light-gray-monochrome: 215,215,215;
    --light-gray-monochrome: 107, 83, 115;
    --gray-monochrome: 75, 54, 82;
    --dark-gray-monochrome: 48,48,52;
    --black-monochrome: 146, 255, 138;
    --pale-accent: 255, 0, 202;
    --bright-accent: 128, 255, 0;
    --medium-accent: 255, 0, 202;
    --dark-accent: 255, 0, 202;
    --alt-accent: 221,102,17;
    --swatch-primary: var(--bright-accent);
    --swatch-primary-darker: var(--medium-accent);
    --swatch-primary-darkest: var(--dark-accent);
    --swatch-border-color: var(--black-monochrome);
    --swatch-text-dark: var(--black-monochrome);
    --swatch-text-light: var(--white-monochrome);
    --swatch-text-general: var(--swatch-text-dark);
    --swatch-important-text: var(--bright-accent);
    --swatch-menubg-color: var(--white-monochrome);
    --swatch-menubg-light-color: var(--pale-gray-monochrome);
    --swatch-menubg-medium-color: var(--light-gray-monochrome);
    --swatch-menubg-medium-dark-color: var(--gray-monochrome);
    --swatch-menubg-dark-color: var(--dark-gray-monochrome);
    --swatch-menubg-black-color: var(--black-monochrome);
    --swatch-menubg-hover-color: var(--black-monochrome);
    --swatch-menutxt-dark-color: var(--black-monochrome);
    --swatch-menutxt-light-color: var(--white-monochrome);
    --swatch-menutxt-general-color: var(--swatch-menutxt-dark-color);
    --swatch-secondary-color: var(--swatch-menubg-light-color);
    --swatch-tertiary-color: var(--swatch-menubg-medium-color);
    --swatch-alternate-color: var(--swatch-menubg-dark-color);
    --swatch-text-secondary-color: var(--swatch-menutxt-light-color);
    --swatch-text-tertiary-color: var(--swatch-menutxt-dark-color);
    --gradient-header: linear-gradient(to top,rgb(var(--header-gradient-color-bottom)) 0%,rgb(var(--header-gradient-color-middle)) 90%,rgb(var(--header-gradient-color-top)) 100%);
    --diagonal-stripes: repeating-linear-gradient(45deg,hsla(0,0%,100%,0),hsla(0,0%,100%,0) 0.25vh,rgba(88,88,88,0.1) 0.35vh,rgba(88,88,88,0.2) 0.5vh);
    --gradient-topmenu: linear-gradient(to bottom,hsla(0,0%,100%,0) 0%,hsla(0,0%,100%,0) var(--header-height-on-desktop),rgba(var(--swatch-topmenu-border-color),1) var(--header-height-on-desktop),rgba(var(--swatch-topmenu-bg-color),1) calc(var(--header-height-on-desktop) + 0.125rem),rgba(var(--swatch-topmenu-bg-color),0.95) calc(100% - 0.125rem),rgba(var(--swatch-topmenu-bg-color),1) calc(100% - 0.125rem),rgba(var(--swatch-topmenu-border-color),1) 100%);
    --gradient-topmenu-mobile: linear-gradient(to bottom,rgba(var(--swatch-topmenu-border-color),1) 0,rgba(var(--swatch-topmenu-bg-color),1) 0.125rem,rgba(var(--swatch-topmenu-bg-color),1) calc(var(--topbar-height-on-mobile) - 0.125rem),rgba(var(--swatch-topmenu-bg-color),1) calc(var(--topbar-height-on-mobile) - 0.125rem),rgba(var(--swatch-topmenu-border-color),1) var(--topbar-height-on-mobile));
    --background-gradient-distance: 40rem;
    --gradient-background: linear-gradient(to bottom,rgba(var(--background-gradient-color),1) 0,rgba(var(--background-gradient-color),0) var(--background-gradient-distance));
    --body-width-on-desktop: 44.25rem;
    --scrollbar-width: 1rem;
    --header-width-on-desktop: 61.25rem;
    --header-width-on-mobile: 100vw;
    --header-height-on-desktop: 7.5rem;
    --header-height-on-mobile: 7.5rem;
    --header-background-image-size: 100% var(--header-height-on-desktop);
    --topbar-height-on-desktop: 1.875rem;
    --topbar-height-on-mobile: 3rem;
    --final-header-height-on-desktop: calc(var(--header-height-on-desktop) + var(--topbar-height-on-desktop));
    --final-header-height-on-mobile: calc(var(--header-height-on-mobile) + var(--topbar-height-on-mobile));
    --sidebar-width-on-desktop: min(calc(var(--base-font-size)*0.93333*19),calc(100vw - var(--body-width-on-desktop)));
    --sidebar-width-on-mobile: calc(var(--base-font-size)*0.93333*17);
    --sidebar-internal-border-thickness: 0.125rem;
    --swatch-background: var(--white-monochrome);
    --background-gradient-color: var(--very-light-gray-monochrome);
    --header-gradient-color-bottom: var(--white-monochrome);
    --header-gradient-color-middle: var(--white-monochrome);
    --header-gradient-color-top: var(--bright-accent);
    --swatch-topmenu-bg-color: var(--swatch-menubg-medium-dark-color);
    --swatch-topmenu-border-color: var(--swatch-menubg-black-color);
    --link-color: var(--pale-accent);
    --visited-link-color: var(--swatch-primary-darkest);
    --hover-link-color: var(--swatch-primary);
    --newpage-color: var(--alt-accent);
}

#header {
    --search-icon-color: var(--black-monochrome);
    --search-icon-hover-color: var(0,0,0,0);
    --search-icon-hover-bg-color: var(--black-monochrome);
    --search-textbox-text-color: 0,0,0,0;
    --search-icon-focus-color: var(--black-monochrome);
    --search-icon-focus-bg-color: var(--black-monochrome);
    --search-focus-outline-color: var(--black-monochrome);
    --login-dropdown-text-color: var(--black-monochrome);
}

#side-bar {
    --sidebar-bg-color: var(--swatch-menubg-color);
    --sidebar-resources-bg-color: var(--swatch-primary-darker),0.25;
    --sidebar-media-bg-color: var(--swatch-primary-darker),0.125;
    --discord-icon-color: 88,101,242;
    --deviantart-icon-color: 0,199,135;
    --facebook-icon-color: 59,89,152;
    --github-icon-color: 25,23,23;
    --instagram-icon-color: 240,148,51;
    --instagram-icon-gradient: linear-gradient(45deg,#f09433,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888);
    --reddit-icon-color: 225,69,0;
    --sandbox-icon-color: 35,140,120;
    --twitch-icon-color: 100,65,165;
    --twitter-icon-color: 29,161,242;
    --youtube-icon-color: 255,0,0;
    --social-icon-hover-color: var(--swatch-primary);
    --sideblock-bg-color: 0,0,0,0;
    --sideblock-heading-border-color: var(--swatch-primary);
    --sideblock-heading-bg-color: 0,0,0,0;
    --sideblock-heading-text-color: var(--swatch-primary-darker);
    --sidebar-border-color: var(--swatch-border-color),0.08;
    --sidebar-subtest-color: var(--swatch-primary);
    --sidebar-links-text: var(--swatch-menutxt-general-color);
    --sidebar-links-bg-color: 0,0,0,0;
    --sidebar-links-hover-bg-color: var(--swatch-primary);
    --sidebar-links-hover-text-color: var(--swatch-text-secondary-color);
    --sidebar-collapsible-fld-link-bg: var(--swatch-primary-darker),0.25;
    --sidebar-collapsible-fld-link-color: var(--sideblock-heading-text-color);
    --sidebar-collapsible-fld-link-hover-bg: var(--swatch-primary-darker);
    --sidebar-collapsible-fld-link-hover-color: var(--swatch-text-secondary-color);
    --sidebar-collapsible-fld-arrow-color: var(--swatch-text-tertiary-color);
    --sidebar-collapsible-fld-arrow-hover-color: var(--swatch-text-secondary-color);
    --sidebar-collapsible-ufld-link-bg: var(--sidebar-collapsible-fld-link-bg);
    --sidebar-collapsible-ufld-link-color: var(--sideblock-heading-text-color);
    --sidebar-collapsible-ufld-link-hover-bg: var(--swatch-primary),1;
    --sidebar-collapsible-ufld-link-hover-color: var(--sidebar-collapsible-fld-link-hover-color);
    --sidebar-collapsible-ufld-arrow-color: var(--sidebar-collapsible-fld-arrow-color);
    --sidebar-collapsible-ufld-arrow-hover-color: var(--sidebar-collapsible-fld-arrow-hover-color);
    --sidebar-collapsible-body-bg: var(--swatch-primary),0.125;
    --sidebar-collapsible-link-color: var(--sidebar-links-text);
    --sidebar-collapsible-link-hover-bg: var(--sidebar-links-hover-bg-color);
    --sidebar-collapsible-link-hover-color: var(--sidebar-links-hover-text-color);
}

#license-area {
    --license-bg-color: #4B3652;
    --license-text-color: var(--bright--accent);
    --license-link-color: var(--bright-accent);
    --license-link-hover-color: var(--swatch-primary);
    --license-link-hover-bg-color: var(--swatch-secondary-color);
}

body{
font-family: "Courier New"
}

::selection {
  background: #FF00CA; /* WebKit/Blink Browsers */
}
::-moz-selection {
  background: #FF00CA; /* Gecko Browsers */
}
div#page-title{
color: #80ff00;
}
div#page-content{
color: #8AFF7B;
}
div#header h1 a::before{
color: #80ff00;
}

div#header h2 ::before{
color: #FF00CA;
  font-weight: bold;
}
div#content-wrap{
background-color: black;
}

#search-top-box form[id=search-top-box-form]:not(:focus-within) input[type=submit] {
    box-shadow: 0 0 0 0 rgb(var(--black-monochrome));
    pointer-events: none;
}
#login-status #account-topbutton {
    background-color: rgb(var(--black-monochrome));
    color: --black-monochrome;
}
#search-top-box form[id=search-top-box-form]:not(:focus-within) input[type=text] {
    background-color: rgba(var(--black-monochrome;),0);
    box-shadow: 0 0 0 0 rgb(var(--black-monochrome;));
    color: rgba(var(--black-monochrome;));
}


#header div[class*=top-bar]>ul>li>a {
color: #80ff00;
}
#header div[class*=top-bar]>ul>li.sfhover a{
color: #80ff00;
}

#login-status a#my-account, #login-status a[href*="account/messages"] {
    color: rgb(var(--black-monochrome));
}
#login-status {
    color: rgb(var(--black-monochrome));
}
#login-status span.printuser {
    color: rgb(var(--black-monochrome));
}
img{
border-radius:15px;
border:solid 2px rgb(var(--accentColor));
}
blockquote{
border-radius: 15px;
}

div#footer {
    --footer-bg-color: black;
    --footer-text-color: var(--bright-accent);
    --footer-link-color: var(--dark-accent);
    --footer-link-hover-color: var(--swatch-primary);
    --footer-link-hover-bg-color: var(--swatch-secondary-color);
}
[[/module]]
[[module CSS]]
.linkOne a{
    color: #FF00CA;
}
.linkOne a:hover{
    color: #80ff00;
    background-color: rgb(0,0,0,0);
}
.linkOne a:visited{
    color: #FF00CA;
}

.linkTwo a{
    color: rgb(0,0,0,0);
}
.linkTwo a:hover{
    color: rgb(0,0,0,0);
    background-color: rgb(0,0,0,0);
}
.linkTwo a:visited{
    color: rgb(0,0,0,0);
}


#side-bar div.menu-item, #side-bar div.menu-item>p{
font-size: 1px !important;

}


[[/module]]
