[[module ListPages limit="@URL|0" fullname="@URL" category="*"]]
通常タグ: %%tags%%
隠しタグ: %%_tags%%
[[/module]]
[[module ThemePreviewer noUi="true"]]
[[iftags]]
[[code type="CSS"]]
#navi-bar, #navi-bar-shadow, #header, #side-bar, #page-title, #breadcrumbs, #page-info, .page-watch-options, .page-options-bottom, .page-tags, #footer, #license-area, #footer-bar, .odialog-shader, .hide-embed, .hiddenDisclaimer, .pollNote, div#container-wrap-wrap::before, div#container-wrap-wrap::after, .wikisys--template-dialog {
  display: none !important;
}
#main-content {
  margin: 0!important;
  padding: 0!important;
  width: 100%!important;
}
body {
  margin: 0!important;
  padding: 0!important;
}
.owindow {
  position: static!important;
  width: 0px;
  height: 0px
}
#ohandle-1 {
  font-size: 0px!important;
}
.modal-header {
  font-size: 0px!important;
}
.button-bar {
  position: absolute;
  top: 0px;
  right: 0px;
}
div#page-content {
  height: 100%;
  min-height: unset;
}
div#content-wrap {
  min-height: unset;
  background: transparent;
}
div#container-wrap {
  background: transparent;
}
[[/code]]
[[/iftags]]
