[[module CSS]]

@keyframes wave {
    0% {text-shadow: 1px 1px 1px white;}
  50%  {text-shadow: 1px 1px 5px #d8e7ff;}
  100% {text-shadow: 1px 1px 1px white;}
}

@import url('https://fonts.googleapis.com/css?family=Open+Sans');

@charset "utf-8";

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Aauthorphage-0/kklogo%20blue.png) 10px 35px no-repeat;
}

#header h1 a {
    display: block;
    content: "yossipossi authorphage";
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Open Sans', Arial, sans-serif;
    font-size: 175%;
    text-decoration: none;
    text-shadow: none;

}

#header h1 a::before {
    content: "yossipossi authorphage";
    color: #000630;
    animation-name: wave;
    animation-duration: 10s;
    animation-iteration-count: infinite;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: black;
    text-shadow: 1px 1px 0px white;
    font-family: Arial, sans-serif;
    font-size: 100%;
}

/* LINKS */

a {
    color: #0075ba !important;
    background: transparent;
}

a.newpage {
    color: #0075ba !important;
}

a:visited {
    color: #0075ba;
    background: transparent;
}

/* TOP BAR */

#top-bar ul li a {
    color: #3f88ff !important;
}

#top-bar ul li:hover a {
   color: #00028c !important;
}

#top-bar ul li ul li a {
    color: #00028c !important;
}

/* RATING MODULE */
.page-rate-widget-box .rate-points {
    background-color: #003399 !important;
    border: solid 1px #aac6ff;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #003399;
    border-top: solid 1px #aac6ff;
    border-bottom: solid 1px #aac6ff;
    font-weight: bold;
    transition: .5s;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: #003399;
    color: #e2fdff;
    padding: 0 4px;
    margin: 0 1px;
    transition: .5s;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #003399;
    color: white;
    text-decoration: none;
    transition: .5s;
}
.page-rate-widget-box .cancel {
    background-color: #003399;
    border: solid 1px #aac6ff;
    border-left: 0;
    border-radius: 0 5px 5px 0;
    transition: .5s;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #e2fdff;
    transition: .5s;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #003399;
    color: #ffffff;
    text-decoration: none;
    transition: .5s;
}

/* PAGE TITLEs */
h1, #page-title {
    color: #0040ad;
}

/* SEARCH BAR */
#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:active {
    border: solid 1px #bad3ff;
    background-color: #002668;
    color: #affdff;
    transition: 1s;
    border-radius: 5px;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #bad3ff;
    background-color: #002c7a;
    transition: 1s;
    border-radius: 5px;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #bad3ff;
    color: white;
    background: #002668;
    border-radius: 2px 2px 2px 2px;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #bad3ff;
    color: white;
    background: #0c3b8c;
    border-radius: 1px 1px 1px 1px;
}

#search-top-box-input:hover, #search-top-box-input:focus {
    background: #002c7a;
    border: 1px solid #bad3ff;
    color: #affdff;
    transition: 1s;
    border-radius: 5px;
}

/* HEADER RECOLOR */

div#container-wrap {
    background: url(https://scp-wiki.wdfiles.com/local--files/fragment%3Aauthorphage-0/GIFMaker.org_sVt5Vt.gif) top left repeat-x;

}

/* SIDEBAR */

#side-bar .side-block h2 {
    color: #0040ad;
}

#side-bar div.side-block {
    box-shadow: 0 2px 6px #00214b !important;
    border: 5px double #00214b;
    background: #eee !important;
}

#side-bar .side-block .heading {
    border-bottom: solid 1px #001735;
}

#side-bar div.menu-item img {
    filter: hue-rotate(310deg);
}

/* TABBERS */

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    background: #1caaff;
    color: white;
    border: 0px solid black;
    border-bottom: 1px solid #70caff;
    transition: 1s;
}

.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a, .yui-navset .yui-navset-top .yui-nav a:hover, .yui-navset .yui-navset-top .yui-nav a:focus {
    background: #1caaff;
    color: white;
    border: 0px solid black;
    transition: 1s;
}

.yui-navset .yui-nav li a em, .yui-navset-top .yui-nav li a em, .yui-navset-bottom .yui-nav li a em {
    border: 0px solid black;
}

.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
    background: #0285d3;
}

.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: 1px solid #636182;
}

/* BLOCKQUOTE */

blockquote {
    border: 1px dashed rgb(0, 137, 150, 100);
    background: #f4f4ff;
    color: black;
}

/* TABLE */

.wiki-content-table {
    border: 2px solid #3f75a5;
    background: #d3eaff;
    color: black;
}

table.wiki-content-table th {
    border-bottom: 2px solid #2c689e;
    background-color: transparent;
    border: 1px solid #2c689e;
}

table.wiki-content-table td {
    background-color: transparent;
    border: 1px solid #2c689e;
}

/* THING IN TOP RIGHT CORNER */

#login-status, #login-status a {
    color: #162ec9;
}

a#account-topbutton {
    border: 0px solid transparent;
}

div#account-topbotton {
    border: 1px solid #4da1ff;
    width: 6em;
}

#account-options li a {
    color: #162ec9;
}

#page-title {
  display: none;
}

.page-title {
  border-bottom: solid 1px #bbbbbb;
  color: #0040ad;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 200%;
}
.page-title p {
  margin: 0;
}

:root {
--borderColor: #0075ba !important;
--bgBadgeColor: #152b69 !important;
}

