[[module CSS]]
@font-face {
    font-family: 'Eigenvector';
    src: url(data:font/truetype;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

/* Basic layout */

body {
    background: #161616;
}

div#container-wrap {
    background-image: url("http://scp-jp-storage.wikidot.com/local--files/file:3266034-490-8ewp/cn3k-header-background.png"), url("http://scp-jp-storage.wikidot.com/local--files/file:3266034-489-1y4u/cn3k-dark-background.png");
    background-color: #161616;
    background-position: center top;
    background-repeat: repeat-x, repeat;
    background-size: auto;
}

div#content-wrap {
    max-width: 100%;
}

div#main-content {
    max-width: 100%;
    margin: 0 5%;
}

div#main-content>div:not(#page-content) {
    max-width: 44.5rem;
    margin: 0 auto;
}

div#page-title {
    display: none;
}

div.scp-rate-wrapper {
    margin: 24px 0;
    display: flex;
    flex-flow: row nowrap;
    justify-content: center;
    align-items: stretch;
}

div.scp-rate-wrapper div.creditRate {
    margin: 0;
}

#page-content h1 {
    color: #D2A34E;
}

#page-content li {
    margin: 1em 0;
}

div.scp-h-stack {
    margin: 24px 0;
    display: flex;
    flex-flow: row nowrap;
    justify-content: center;
    align-items: stretch;
    gap: 24px;
}

div.scp-v-stack {
    flex: 0 1 640px;
    max-width: 100%;
    display: flex;
    flex-flow: column nowrap;
    align-items: stretch;
    gap: 24px;
}

div.scp-v-stack.scp-no-gap {
    gap: 0;
}

div.scp-v-stack.scp-small {
    flex: 0 1 384px;
}

div.scp-v-stack.scp-medium {
    flex: 0 1 512px;
}

div.scp-block {
    background: #F4F0EA;
    color: #161616;
    padding: 2em;
    flex-grow: 1;
}

div.scp-block.scp-reply {
    background: #E4C183;
}

@media only screen and (max-width: 1040px) {
    div#container-wrap {
        background-size: auto, contain;
    }

    div.scp-h-stack {
        display: block;
    }

    div.scp-v-stack {
        display: block;
        width: 100%;
    }

    div.scp-v-stack.scp-small {
        width: 100%;
    }

    div.scp-block {
        margin: 24px 0;
    }

    div.scp-v-stack.scp-no-gap div.scp-block {
        margin: 0;
    }
}

div.scp-intro-text {
    color: white;
    font-size: 1.25em;
    width: 640px;
    max-width: 100%;
    margin: 0 auto;
}

div.scp-character-info {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
    gap: 16px;
}

div.scp-reply-info {
    display: flex;
    flex-flow: row-reverse nowrap;
    align-items: center;
    margin-bottom: 8px;
}

div.scp-character-info-head {
    width: 64px;
    height: 85px;
    box-shadow: inset 0 0 0 1px #00000016;
    background-position: center center;
    background-size: cover;
}

div.scp-character-info-bar {
    width: 12px;
    height: 85px;
}

div.scp-character-info-details {
    flex-grow: 1;
}

div.scp-character-info-details p {
    line-height: 200%;
}

span.scp-eigenvector {
    font-family: 'Eigenvector';
    font-size: 2em;
    vertical-align: middle;
}

span.scp-public-key {
    display: inline-block;
    padding: 4px 0;
    font-family: 'Eigenvector';
    font-size: 2.5em;
}

div.scp-block.scp-node-intro {
    margin-top: 96px;
}

@media only screen and (max-width: 1040px) {
    div.scp-block.scp-node-intro {
        margin-top: 128px;
    }
}

div.scp-node-profile {
    width: 96px;
    height: 128px;
    margin: -96px auto 24px auto;
    box-shadow: inset 0 0 0 1px #00000016;
    background-position: center center;
    background-size: cover;
}

div.scp-museum-logo {
    width: 256px;
    height: 256px;
    background: transparent;
    background-image: url("http://scp-jp-storage.wdfiles.com/local--files/file:3266034-491-dbr5/cn3k-museum-logo.png");
    background-size: cover;
    background-position: center center;
    margin: 0 auto;
}

div.scp-intro-image-block {
    width: 768px;
    max-width: 100%;
    margin: 0 auto;
    border: solid 1px #F4F0EA;
    background: #F4F0EA;
}

div.scp-intro-image-block img {
    width: 100%;
}

div.scp-intro-image-caption {
    text-align: center;
    color: #161616;
}

div.scp-intro-image-caption p {
    margin: 4px 1em;
    padding: 0;
}

span.scp-void-text {
    font-weight: bold;
    color: #D2A34E;
}

div.scp-block a,
div.scp-block a:visited {
    color: #161616;
    text-decoration: underline;
}

div.scp-block a:hover {
    font-style: italic;
}

