/* Non-Content */

div.rate-box-with-credit-button {
    background-color: #D2A34E;
    border: solid 1px #D2A34E;
}

div.page-rate-widget-box .rate-points {
    background-color: #D2A34E !important;
    border: solid 1px #D2A34E;
}

div.page-rate-widget-box .cancel {
    background-color: #D2A34E;
    border: solid 1px #D2A34E;
}

div.page-rate-widget-box .rateup,
div.page-rate-widget-box .ratedown {
    background-color: #F4F0EA;
    border-top: solid 1px #D2A34E;
    border-bottom: solid 1px #D2A34E;
}

div.page-rate-widget-box .rateup a,
div.page-rate-widget-box .ratedown a {
    color: #161616;
}

div.page-rate-widget-box .rateup a:hover,
div.page-rate-widget-box .ratedown a:hover {
    background: #D2A34E;
    color: #F4F0EA;
}

div.page-rate-widget-box .cancel a {
    color: #000000;
    opacity: 0.5;
}

div.page-rate-widget-box .cancel a:hover {
    color: #F4F0EA;
    background: #D2A34E;
    opacity: 1;
}

div.creditButton p a {
    color: #F4F0EA;
    border-left: solid 1px #161616;
}

div.creditButton p a:hover {
    color: #161616;
}

#search-top-box-form input[type='submit'] {
    background: #D2A34E;
    color: #F4F0EA;
}

#search-top-box-form input[type='submit']:hover,
#search-top-box-form input[type='submit']:focus {
    background: #D2A34E;
    color: #F4F0EA;
}

#search-top-box-input {
    background-color: #161616;
}

