[[module CSS]]
@import url('https://scp-wiki.wdfiles.com/local--files/theme%3A3law/station-font.css');

/*
    Third Law Canon Theme
    [2016 Wikidot Theme]
    Created by GreenWolf, with technical assistance from Randomini and aesthetic assistance from Dr Kens and tawny.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan.
    The Station font was created by Nick Matavka and is licensed under the SIL Open Font License.
    The Prometheus Labs/Third Law Flame Logo was created by GreenWolf and is licensed under CC-BY-SA 3.0.
     Revised by stormbreath.
    ColorlessL 基于以上版式修改
    图标来自easyicon，基于Creative Commons Attribution-Share Alike 3.0 Unported License分享。
    原图标链接：https://www.easyicon.net/1171206-pen_leather_icon.html
*/

/* COMMON */
h1, #page-title {
    color: #86bbd8;
}

#page-title {
    border-color: #86bbd8;
}

/* links */
a, #side-bar a {
    color: #88ccff;
}

a.newpage, #side-bar a.newpage{
    color: #aaddff;
}

a:visited, #side-bar a:visited{
    color: #4488dd;
}

body {
    background-color: #666666;
    color: #eeeeee;
}
 blockquote{
    -webkit-margin-start: 0px;
    -webkit-margin-before: 1em;
    -webkit-margin-after: 1em;
    -webkit-margin-end: 0px;
    border: 1px solid #888;
    border-radius: 1px;
    background-color: #444444;
}
div#container-wrap {
    background: url('http://scpsandboxcn.wikidot.com/local--files/colorless-note/body_bg.png') top left repeat-x;
}

/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: url(http://scpsandboxcn.wdfiles.com/local--files/colorless-note/pen_leather_96icon.png) 120px 35px no-repeat;
}

#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #444 !important;
}

#search-top-box-input {
    background-color: #3a3a3a !important;
    color: white !important;
}

#search-top-box-form input[type="submit"] {
    background: none !important;
    background-color: #3a3a3a !important;
    color: white !important;
}

#login-status {
    color: #e9ebf0;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #e9ebf0;
}
#login-status ul a {
    color: #4057a7;
    background: transparent;
}

#account-topbutton {
    background: #CCCCCC;
    color: #4057a7;
}

#header h1 a {
    color: transparent;
    font-family: 'stationmedium', Arial, sans-serif;
    font-size: 180%;
    text-shadow: none;
}

#header h1 a::before {
    content: "Content Loading...";
    color: #e9ebf0;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'We all came from There.';
    color: #F1F1F1;
    font-style: italic;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* TOP MENU */
#top-bar a {
    color: #e9ebf0;
}

#top-bar ul li ul {
    border: solid 1px #4B4194;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #E3E7EA;
    color: #88ccff;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #6A7194;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #555555;
}

#top-bar ul li ul a, #top-bar a:hover {
    color: #88ccff;
}


/* SIDE-MENU */

#side-bar a:visited,
#interwiki a:visited {
   color: #88ccff;
}

#side-bar .side-block .menu-item > img {
     filter: hue-rotate(45deg) brightness(80%) invert(90%);
    -webkit-filter: hue-rotate(45deg) brightness(80%) invert(90%);
}

#side-bar div.menu-item > .img
#interwiki div.menu-item > .img {
    filter: hue-rotate(135deg);
    -webkit-filter: hue-rotate(135deg);
}

#side-bar .side-block,
#interwiki .side-block {
    border: 1px solid #86bbd8;
    background: #555555 !important;
    box-shadow: none;
}

#side-bar .heading,
#interwiki .heading{
    color: #86bbd8;
    border-bottom: solid 1px #86bbd8;
}

#side-bar div.menu-item.inactive a {
    color: #315B7D;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #86bbd8;
}

#side-bar .collapsible-block-folded {
    background: none;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #86bbd8;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #86bbd8;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #88ccff;
}
div.scpnet-interwiki-wrapper {
    filter: hue-rotate(45deg) brightness(80%) invert(90%);
}

/* YUI-TABS */
.yui-navset .yui-content{
    background-color: #555555;
    padding-left: 2em;
    padding-right: 2em;
    border: 1px solid black;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #36648B;
    border: 1px solid black;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #36648B; /* selected tab background */
    color: #eeeeee;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #bfc3d4;
    text-decoration: none;
    border: 1px solid black;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border: 0px solid black;
}
.yui-navset li {
    line-height: normal;
}
/* FOOTER */
#footer {
    clear: both;
    font-size: 77%;
    background: #7F7C94;
    color: #C1BCE0;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer .options {
    visibility: visible;
    display: block;
    float: right;
    width: 50%;
    font-size: 100%;
    text-align: right;
}
#footer a {
    color: #E0E0E0;
    background: transparent;
}

/* SOME NICE BOXES */
div.sexy-box {
    background: #E0E0E0;
    border: 1px solid #C1BCE0;
}

div.sexy-box div.image-container img {
    border: 1px solid #E0E0E0;
}

.content-panel {
    background-color: #1F1F1F;
    margin: 10px 0 15px;
}

.content-panel.standalone {
    background: #E0E0E0;
}

.content-panel .panel-heading {
    color: #E0E0E0;
}

.content-panel .panel-body {
    background: #E0E0E0;
}

.content-panel .panel-footer {
    color: #E0E0E0;
}
.content-panel .panel-footer a {
    color: #E0E0E0;
}

.content-panel .content-toc {
    background-color: #E0E0E0;
}

/* Page Rating Module Customizations */
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #555555!important;
    border: solid #555555 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #555555!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #f0f0f0;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #d4d4d4!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #323232!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

/* Standard Image Block */
.scp-image-block {
    border: solid 1px #4B4194;
    box-shadow: 0 1px 6px rgba(0,0,0,.25);
    width: 300px;
}

.scp-image-block.block-right {
        float: right;
    clear: right;
    margin: 0 2em 1em 2em;
}

.scp-image-block.block-left {
    float: left;
    clear: left;
    margin: 0 2em 1em 0;
}

.scp-image-block.block-center {
    margin-right: auto;
    margin-left: auto;
}
.scp-image-block img {
    border: 0;
    width: 300px;
}
.scp-image-block .scp-image-caption {
    background-color: #666666;
    border-top: solid 1px #4B4194;
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    width: 300px;
}
.scp-image-block > p {
    margin: 0;
}
.scp-image-block .scp-image-caption > p {
    margin: 0;
    padding: 0 10px;
}

@media (min-width: 767px) {
     #header {
             background-position: 10px 20px;
     }
}

@media (max-width: 767px) {
    #side-bar {
        background-color: rgb(54, 100, 139);
    }
}

/* Preview module */

.third-law-preview {

    display: none;

}


/* ---- ADJUST MOBILE IMAGE SIZE ---- */
 @media (max-width: 767px){
     #side-bar:target + #main-content{
         left: 0;
    }
}
 @media (min-width: 768px){
     #header h1 a{
         font-size: 150%;
    }
}
[[/module]]
