[[module css]]


.no-sel  tt{
-moz-user-select: none;
-khtml-user-select: none;
user-select: none;
    color:#555;
    padding:0 0rem 0 0rem;
    background: none;
}

.no-sel tt:before {
content:" ←  "
}


.item-tt {
    border-top: 3px solid #6d6b82;
    border-bottom: 3px solid #6d6b82;
    padding:0 0.5rem;
    margin: 3em;

}

.item-tt h2, .item-tt h3{
    border-top: 1.5px solid #6d6b82;
    border-bottom: 1.5px solid #6d6b82; 
    margin: 0.1rem;
    padding: 0.5rem;
    font-size: 160%;
    text-align: center;
}

.mmposter-wrap {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    margin: 0 auto;
    left: 2rem;
    position: relative;
}

.mmposter-cell {
    width: 48%;
    justify-items: center;
}

@media (max-width: 700px) {
    .mmposter-wrap {
        left: 0rem;
    }
    .mmposter-cell {
        width: 100%;
margin-top:2rem;
    }
    .mob-breakkkk {
        display: block
    }

}

.lc-mid{
    display:  inline;
    width: auto;
        flex-shrink:0;
}

.lc-wrap{
    display:flex;
    align-items:center;
}

.lc-wrap:after,
.lc-wrap:before{
    align-items:center;
    flex-basis:100%;
    flex-shrink:2;
    content: ' ';
}


/**/
.et-plank {
    margin: -0.5rem 0 0 0;
}

.book-list a {
    margin: 0 0 0 0.5rem;
}


/**/
.tt-txt *{
font-family:'noto serif sc', sans-serif !important;
}

.tt-line-top, .tt-line-bottom {
    background: none !important;
     margin: 0rem 0 0rem 0;
    padding: 0;
   height: 1rem;
}
.tt-ch {
    margin: 0rem 0 0rem 0;
    padding: 0;

}

