     box-shadow:
     inset 0 0.3125rem 0 rgba(var(--white-monochrome)),
     /* Top 1 */
     inset 0.234375rem 0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Top Left 2 */
     inset 0.3125rem 0 0 rgba(var(--light-gray-monochrome), 0),
     /* Left 3 */
     inset 0 -0.3125rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom 4*/
     inset 0.234375rem -0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom Left 5 */
     inset -0.234375rem -0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom Right 6 */
     inset -0.3125rem 0 0 rgba(var(--light-gray-monochrome), 0),
     /* Right 7 */
     inset -0.234375rem 0.234375rem 0 rgba(var(--white-monochrome));
     /* Top Right 8 */
     }

     100% {
     box-shadow:
     inset 0 0.3125rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Top 1 */
     inset 0.234375rem 0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Top Left 2 */
     inset 0.3125rem 0 0 rgba(var(--light-gray-monochrome), 0),
     /* Left 3 */
     inset 0 -0.3125rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom 4*/
     inset 0.234375rem -0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom Left 5 */
     inset -0.234375rem -0.234375rem 0 rgba(var(--light-gray-monochrome), 0),
     /* Bottom Right 6 */
     inset -0.3125rem 0 0 rgba(var(--light-gray-monochrome), 0),
     /* Right 7 */
     inset -0.234375rem 0.234375rem 0 rgba(var(--light-gray-monochrome), 0);
     /* Top Right 8 */

     }
     }

     @keyframes rotate {
     0% {
     transform: rotate(0deg);
     }

     100% {
     transform: rotate(360deg);
     }
     }
     @media only screen and (max-width: 400px) {
     .continue-button {
     font-size: 1em;
     }
     }

    </style>
</head>

<body>
    <div id="continue-button-id" class="continue-button">
     <a id="continue-link" href="http://scp-wiki-cn.wikidot.com/scp-4485/offset/3"></a>
     <div class="accept">点击报批</div>
     <div class="supervisor">主管许可</div>
     <br>
     <span>✔</span>
    </div>
    <script>
        var continueButton = document.getElementById("continue-button-id");  
        var continueLink = document.getElementById("continue-link");
        var to = null;     

        function mouseOver() {
            if(to){
                clearTimeout(to);
            }           
            to = setTimeout(function(){                 
                continueButton.classList.remove("active-hover");
                continueLink.classList.add("active-link");                    
            }, 5500);
        }

        function mouseOut() {
            if(to){
                clearTimeout(to);
            }
            continueLink.classList.remove("active-link");
        }        

        continueLink.addEventListener("mouseover", mouseOver);
        continueLink.addEventListener("mouseout", mouseOut);
    </script>
</body>

[[/html]]
[[/div]]
