[[include :scp-wiki-cn:theme:the-foundation-theme]]
[[module css]]
:root {
    --rating-module-text-color: var(--swatch-menutxt-light-color);
    --rating-module-button-color:  var(--swatch-menutxt-light-color);
}

#page-content > div:nth-child(1) {
    padding: 0.5rem 0.5rem 0 0.5rem;
}

.page-rate-widget-box {
    margin: 0;
    background:
     linear-gradient(0deg,
     rgba(var(--swatch-primary-darkest),.55) 0,
     rgba(var(--swatch-primary-darkest),.45)) !important;
}
[[/module]]
[[module css]]
.page-title {
    margin-top: 1rem;
    min-height: 4rem;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    width: -webkit-calc(100% - 1em - 0.5rem);
    width: -moz-calc(100% - 1em - 0.5rem);
    width: calc(100% - 1em - 0.5rem);
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    border: initial;
    font-size: 3em;
    line-height: 1;
    text-align: center;
    color: rgb(var(--white-monochrome));
    background-color: rgb(var(--swatch-primary));
    padding: 0.5em;
    border: 0.25rem solid rgb(var(--white-monochrome));
    position: relative;
    z-index: 1;
    font-family: var(--title-font);
    font-weight: 900;
}

.page-title::before {
    content: " ";
    pointer-events: none;
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-image: url(https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg);
    background-repeat: no-repeat;
    background-position: center center;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    background-color: rgb(var(--swatch-primary));
    z-index: -1;
    opacity: 0.25;
}

.warning-container iframe {
    min-height: 9.375rem;
}

.warning-container,
.warning-container * {
    -webkit-box-sizing: border-box;
       -moz-box-sizing: border-box;
            box-sizing: border-box;
}

.warning-container {
    width: 90%;
    margin: 0 auto;
    background-color: rgb(var(--dark-gray-monochrome));
    color: rgb(var(--swatch-text-light));
    font-weight: 900;
    border: 0.5em solid rgb(var(--swatch-primary-darkest));
}

.warning-container .warning-top {
    font-size: 200%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    padding: 1rem;
    font-family: var(--title-font);
    background-color: rgb(var(--swatch-primary));
    background-image: url("http://scptestwiki.wikidot.com/local--files/members:wt-framing-theme/cons_circle.svg");
    -webkit-background-size: 50% 50%;
    -moz-background-size: 50%;
    -o-background-size: 50%;
    background-size: 50%;
    background-attachment: fixed;
    background-position: 50% 50%;
}

.warning-container .warning-body {
    font-weight: 100;
    padding: 1rem;
    margin: 1rem;
    line-height: 1.5;
    font-size: 115%;
    font-family: var(--mono-font);
    background-color: rgb(var(--swatch-primary-darkest),0.25);
}

table.wiki-content-table {
    width: 100%;
    font-size: 1.5em;
    text-align: center;
    margin: 0 auto;
}

table.wiki-content-table,
table.wiki-content-table * {
    border-color: rgb(var(--black-monochrome)) !important;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

table.wiki-content-table tr:last-of-type {
    width: 100%;
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-box-align: stretch;
    -webkit-align-items: stretch;
    -moz-box-align: stretch;
    -ms-flex-align: stretch;
    align-items: stretch;
    margin: 0;
    font-weight: 700;
}

table.wiki-content-table td {
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    margin: 0;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    -webkit-flex-basis: 50%;
    -ms-flex-preferred-size: 50%;
    flex-basis: 50%;
    background-color: rgb(var(--swatch-primary-darkest));
    color: rgb(var(--white-monochrome));
    text-transform: uppercase;
    line-height: 1;
    text-align: center;
}

table.wiki-content-table td * {
    width: 100%;
}

table.wiki-content-table td span:nth-of-type(even) {
    font-family: var(--mono-font);
    font-size: 0.75em;
}

@media only screen and (max-width: 768px) {
    .page-title {
     font-size: 2em;
    }

    table.wiki-content-table tr {
     -webkit-flex-wrap: wrap;
         -ms-flex-wrap: wrap;
             flex-wrap: wrap;
    }

    table.wiki-content-table tr > td {
     min-width: 100%;
     font-size: 1rem;
     padding: 0.5rem;
    }
}
@media (max-width: 500px ) {
    .warning-container .warning-body {
     padding: 0.5rem 1rem;
     margin-bottom: 1.5rem;
     font-size: 95%;
    }
}
