[[include :scp-wiki:theme:monotypical]]
[[module CSS]]
/* NORD */
.gambit {
       --linkColor: rgb(160, 44, 44) !important;
}

.reanimation {
       --linkColor: #652db6 !important;
}

.twisted-pines {
      --linkColor: #243b78!important;
}

.clockwork {
      --linkColor: #b49a5a!important;
}

.hive-mind {
      --linkColor: #8f3233!important;
}

.eurtec {
       --linkColor: #081820!important;
}

.wilsons {
       --linkColor: #a3c162!important;
}

.dental {
       --linkColor: #ece3de!important;
}
.dental .collapsible-block-link:hover, .dental a:hover {
       --textColor: unset;
}

:root {
    --linkColor: #88C0D0;

    --primaryBackgroundColor: #ECEFF4;
    --secondaryBackgroundColor: #E5E9F0;
    --tertiaryBackgroundColor: #D8DEE9;
    --borderColor: #4C566A;

    --shadow: rgba(46,52,64,0.25);

    --selectionColor: #EBCB8B;
    --textColor: #2E3440;
    --textColor-alt: #434C5E;
}
#side-bar, #license-area {
    --secondaryBackgroundColor: #2E3440;
    --tertiaryBackgroundColor: #434C5E;
    --textColor: #D8DEE9;
}
.authorlink-wrapper > a::before {color: #0E0E0E !important;}
.collapsible-block-link:hover, a:hover {
       --textColor: #ededed;
}
