[[module css]]
/*
    None CSS with Left Beef
    [2019 Wikidot Theme]
    Created by stormbreath.
    Edited by DrTuring
*/

/* LINKS */
a {
    color: #0645AD;}

a.newpage {
    color: #CC2200;}

 a:visited {
     color: #0B0080;
}

#side-bar a {
     color: black
}

#side-bar a:visited {
     color: black;
}

/* REMAKE THE HEADER */

h1, #page-title {
    color: black;
}

#page-title {
    border-color: black;
}

div#container-wrap {
    background: url(none);
}

div#extra-div-1 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: black;
}

#header {
    background-image: url(http://scp-ru.wdfiles.com/local--files/list-of-foundation-s-internal-departments/eng.png);
    background-size: 6rem;
}
@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }
}

#header h1 a {
    color: transparent;
    text-shadow: none;
}

#header h1 a::before {
    content: 'SCP基金会工程和技术服务部门';
    color: black;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: '设计，建造，维护';
    color: black;
}

/* SIDE-BAR */
#side-bar .side-block {
    background-color: white !important;
    border-color: transparent;
    border-radius: 0px;
    box-shadow: none;
}

#side-bar .heading {
    color: black;
    border-bottom: solid 1px black;
}

@media (max-width: 767px) {
    #side-bar {
         background-color: white;
    }

    #side-bar:target .close-menu {
         background: none;
    }
}

#side-bar .collapsible-block-folded {
    background-image: url(http://scp-wiki.wdfiles.com/local--files/theme%3Aminimal/expand.png);
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px black;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: black;
}

#side-bar img,
iframe.scpnet-interwiki-frame {
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}

/* TOP-BAR */
#top-bar ul li ul {
    border: solid 1px black;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid white;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: white;
    color: black;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: black;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: black;
    color: white; /* top bar hover background color */
}

/* RATING MODULE */
.page-rate-widget-box .rate-points {
    background-color: black !important;
    border: solid 1px black;
    text-transform: capitalize;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: black;
    border-top: solid 1px black;
    border-bottom: solid 1px black;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: white;
    color: black;
}
.page-rate-widget-box .cancel {
    background-color: black;
    border: solid 1px black;
}
.page-rate-widget-box .cancel a {
    color: white;
}
.page-rate-widget-box .cancel a:hover {
    background: white;
    color: black;
}

#page-content .rate-box-with-credit-button {
    background-color: black;
    border: 1px solid black;
    border-radius: 5px !important;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}

.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
     color: black;
     background-color: white;
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(102, 102, 102, .5);
}

.close-credits,
.credit-back {
    filter: hue-rotate(260deg);
}

/* PAGE ELEMENTS */
blockquote,
div.blockquote,
#toc,
.code {
     background-color: white;
}

.scp-image-block {
    border: solid 1px black;
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    background-color: white !important;
    border-top: solid 1px black !important;
    color: black;
}

/* RECOLOR THE SEARCH BAR */
#search-top-box-input {
    border-color: black;
    color: black !important;
    background-color: white;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: white;
    border-color: black;
}

#search-top-box-form input[type=submit] {
    border: solid 1px black;
    color: black;
    background-color: white;
    background: white;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px black;
    background-color: white;
    background: white;
    color: black;
}

/* LOGIN STUFF */
#login-status {
    color: black;
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}
#login-status a {
    color: black;
}
#login-status ul a {
    color: black;
}
#account-topbutton {
     background: #ccc;
     color: black;
}

/* HOVER THINGIES */
.hovertip {
     background: white !important;
}

/* FOOTER */
#footer {
    background: black;
    color: white;
}

/* TABS */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent; /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: white; /* tab background */
    border-color: white;
    color: black;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
    background: white;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    border-color: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: white; /* selected tab background */
    color: black;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px 0.5px rgba(0,0,0,0.5);
    background: white;
}

.yui-navset .yui-content {
    background: transparent; /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: transparent; /* content border */
    border-top-color: transparent; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

.creditRate .heritage-rating-module,
.heritage-rating-module {
    display: none;
}

/* Grid layout */

.left-grid {
    float: left;
    width: 49%;
    height: 110px;
}
.right-grid {
    float: right;
    width: 49%;
    height: 110px;
}

/* Mobile Media Query */
@media (max-width: 479px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}
[[/module]]

[[html]]
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
body {font-family: Verdana;}
* {box-sizing: border-box;}

.input-container {
    display: -ms-flexbox; /* IE10 */
    display: flex;
    width: 100%;
    margin-bottom: 15px;

}

.input-field:focus{
border-style: solid;
transition: 0.5;
border-color: maroon;
border-width: 2px;

}

.input-field {
    width: 100%;
    padding: 10px;
    outline: none;
    transition: 0.5s;

}

.btn:focus {
  outline: 0;
}

.btn {
  background-color: Maroon;
  color: white;
  padding: 10px 20px;
  border: none;
  cursor: pointer;
  width: 50%;
  opacity: 0.9;
  transition: 0.09s;
  border-radius: 3px;
}

.btn:hover {
  opacity: 1;
  transition: 0.8s;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.7);
}

.btn:active {
  transition: 0.09s;
  transform: translateY(1px);
  box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0);
}

.btn2 {
    background-color: white;
    border: none;
    cursor: pointer;

}

p{
font-family: Helvetica;
margin-bottom: 5%;
}
</style>
</head>
<body>

<form autocomplete="off" style="max-width:450px;text-align:center;margin: auto;">
<p style="font-size:55px; font-weight: bold;">SCiPNET 登入</p>
  <div class="input-container">
    <input class="input-field" type="text" placeholder="用户名" name="usrnm">
  </div>

  <div class="input-container">
    <input onkeyup="if(this.value.length > 0) document.getElementById('start_button').disabled = false; else document.getElementById('start_button').disabled = true;" class="input-field" type="password" placeholder="密码">
  </div>

   <button id="start_button" class="btn btn-success" onclick="window.open('http://scp-wiki-cn.wikidot.com/drturing-s-personnel-file/offset/2','_parent')" disabled>登入</button>

<br></br>
   <button style= "font-weight: bold;" class="btn2 btn-success">忘记密码？</button>

</form>
</body>
</html>
[[/html]]
