[[module CSS]]
.content-panel .content-type-title>p {
    margin: 0;
}
.content-panel .content-type-title {
    font-size: 120%;
    font-weight: bold;
    padding: 5px 20px;
    background-color: #666;
    color: #fff;
    border-radius: 8px 8px 0 0;
    box-shadow: inset 0 1px 1px rgba(255, 255, 255, .8), inset 0 15px 1px rgba(255, 255, 255, .2), inset 0 15px 10px rgba(255, 255, 255, .2);
}
.content-panel .content-type-description {
    padding: 0 20px;
    text-align: justify;
    padding: 5px 20px;
}
.content-panel.content-row .content-type-description-2 {
    padding: 0 0.5em;
    text-align: justify;
}
.content-panel-table, .content-panel-table-row, .content-panel-table-cell {
    border: 1px solid #000;
    border-collapse: collapse;
}
.content-panel-table-cell {
    vertical-align: top;
    padding: 0 2px;
}
table.wiki-content-table {
    width: 100%;
    margin-top: 0;
}
table.wiki-content-table tbody {
    border: none;
}
table.wiki-content-table tr {
    border: none;
    border-bottom: 1px solid #888;
}
table.wiki-content-table th {
    border: none;
    font-size: 1.5em;
    padding-top: 0.5em;
    background-color: transparent;
    text-align: center;
}
table.wiki-content-table td {
    border: none;
    width: 50%;
}
.rowt {
    font-weight: bold;
    color: #fff;
    background-color: #666;
}
.celt {
    padding: 0 2px;
    width: 50%
}
.celd {
    padding: 0 2px;
    text-align: center;
}
.alph {
    width: 100%;
    font-weight: bold;
    font-size: 150%;
    color: #901;
    margin-top: 1.5em;
    border-bottom: 1px solid #666;
}
table.alph tr {
    border: none;
    border-top: 1px solid #888;
}
table.alph td {
    border: none;
    text-align: center;
    width: 50%;
}
[[/module]]


