[[module css]]

/* HAS CHECK */

.has-warning {display: none;}
@supports not selector(:has(.collapsible-block-unfolded[style="display: block;"] span.act1)) {
  .has-warning {display: block;}
  .nan-warning {display: none;}
}

/* content */

span.hidden {
  display: none;
}

.flexy {
  display: flex;
}

.flexy > div.code {
  border: none;
  padding: 0;
  margin: 0;
  margin-left: 1em;
  line-height: 1.2rem;
  width: 100%;
}

.codenum {
  width: 2em;
  border-right: 1px solid black;
  counter-reset: lineCount;
  margin: 1em 0;
}

.codenum p {
  counter-increment: lineCount;
  margin: 0;
  text-align: right;
  margin-right: 1em;
  line-height: 1.2rem;
}

.codenum p::before {
  content: counter(lineCount);
}

.coderun {
  width: 1em;
  margin: 1em 0;
  margin-left: 1em;
}

.coderun p, .coderun a {
  width: 2em;
  margin: 0;
  line-height: 1.2rem;
}

.coderun .collapsible-block span {
  display: block;
  font-size: 0;
  height: 0;
}

/* painter */

.show-area {
  display: none;
  position: fixed;
  bottom: 2em;
  left: 2em;
  width: min(400px, 80vw);
  aspect-ratio: 4 / 3;
  border-radius: 5px;
  overflow: hidden;
  background-color: black;
  border: 1px solid black;
  flex-direction: column;
  z-index: 7;
}

.info-bar {
  background-color: #05e;
  color: white;
  font-size: 144%;
  font-weight: bold;
  font-family: serif;
}

.info-bar > p {
  margin-top: 0.25em;
  margin-bottom: 0.25em;
  margin-left: 0.5em;
}

:root { counter-reset: time 0; }

span.days { counter-increment: time 1; }

div.world-content {
  position: relative;
  width: 100%;
  height: 100%;
  background-color: white;
  display: none;
}

div.world-content::before {
  content: "DAY " counter(time);
  background-color: white;
  border: 3px solid orangered;
  padding: 0.25rem;
  position: absolute;
  top: 1rem;
  right: 1rem;
  display: none;
}

.world-content > .structures {
  display: none;
  flex-flow: row nowrap;
  align-items: flex-end;
  height: 100%;
}

.struct {
  flex: 1 1 50px;
  height: var(--struct-height);
  background-color: #000000a0;
}

.anchor-a {
  position: absolute;
  top: 45%;
  left: 1em;
  display: none;
}

.anchor-b {
  position: absolute;
  top: 45%;
  right: 1em;
  display: none;
}

.world-content a {
  background-color: #0008;
  padding: 0.2em 0.4em;
  text-decoration: currentcolor solid underline;
}

@media (max-width: 640px) {
  :root:has(.collapsible-block-unfolded[style=""] span.act3, .collapsible-block-unfolded[style="display: block;"] span.act3) div.world-content::before { content: "DAY 1"; }
  :root:has(.collapsible-block-unfolded[style=""] span.act4, .collapsible-block-unfolded[style="display: block;"] span.act4) div.world-content::before { content: "DAY 2"; }
  :root:has(.collapsible-block-unfolded[style=""] span.act5, .collapsible-block-unfolded[style="display: block;"] span.act5) div.world-content::before { content: "DAY 3"; }
  :root:has(.collapsible-block-unfolded[style=""] span.act6, .collapsible-block-unfolded[style="display: block;"] span.act6) div.world-content::before { content: "DAY 4"; }
  :root:has(.collapsible-block-unfolded[style=""] span.act7, .collapsible-block-unfolded[style="display: block;"] span.act7) div.world-content::before { content: "DAY 5"; }
  :root:has(.collapsible-block-unfolded[style=""] span.act8, .collapsible-block-unfolded[style="display: block;"] span.act8) div.world-content::before { content: "DAY 6"; }
}

:root:has(.collapsible-block-unfolded[style=""] span.act1, .collapsible-block-unfolded[style="display: block;"] span.act1) div.show-area {
  display: flex;
}

:root:has(.collapsible-block-unfolded[style=""] span.act2, .collapsible-block-unfolded[style="display: block;"] span.act2) div.world-content {
  display: block;
}

:root:has(.collapsible-block-unfolded[style=""] span.act3, .collapsible-block-unfolded[style="display: block;"] span.act3) div.world-content::before {
  display: block;
}

:root:has(.collapsible-block-unfolded[style=""] span.act4, .collapsible-block-unfolded[style="display: block;"] span.act4) div.world-content {
  background: linear-gradient(217deg, #00c5d0c0, #00c5d000 70.71%),
              linear-gradient(127deg, #a8aaffc0, #a8aaff00 70.71%),
              linear-gradient(336deg, #61b9ffc0, #61b9ff00 70.71%);
}

:root:has(.collapsible-block-unfolded[style=""] span.act5, .collapsible-block-unfolded[style="display: block;"] span.act5) div.structures {
  display: flex;
}

:root:has(.collapsible-block-unfolded[style=""] span.act6, .collapsible-block-unfolded[style="display: block;"] span.act6) div.structures {
  display: grid;
  grid-template-areas:
      "s s s s s s s s"
      ". . c . . . g ."
      ". b c . e f g ."
      "a b c . e f g h"
      "a b c d e f g h";
  grid-template-columns: repeat(8, 1fr);
  grid-template-rows: 20px repeat(4, 1fr);
  column-gap: 10px;
}

:root:has(.collapsible-block-unfolded[style=""] span.act6, .collapsible-block-unfolded[style="display: block;"] span.act6) .struct {
  grid-area: var(--area);
  height: 100%;
}

:root:has(.collapsible-block-unfolded[style=""] span.act7, .collapsible-block-unfolded[style="display: block;"] span.act7) .struct {
  background: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla/struct_bg.svg) top / contain repeat-y;
}

:root:has(.collapsible-block-unfolded[style=""] span.act8, .collapsible-block-unfolded[style="display: block;"] span.act8) .anchor-a, :root:has(.collapsible-block-unfolded[style=""] span.act8, .collapsible-block-unfolded[style="display: block;"] span.act8) .anchor-b {
  display: block;
}

:root:has(.collapsible-block-unfolded[style=""] span.act9, .collapsible-block-unfolded[style="display: block;"] span.act9) .world-content a:link {
  color: royalblue;
}

:root:has(.collapsible-block-unfolded[style=""] span.act9, .collapsible-block-unfolded[style="display: block;"] span.act9) .world-content a:visited {
  color: violet; 
}

:root:has(.collapsible-block-unfolded[style=""] span.act9, .collapsible-block-unfolded[style="display: block;"] span.act9) .world-content a:hover, :root:has(.collapsible-block-unfolded[style=""] span.act9, .collapsible-block-unfolded[style="display: block;"] span.act9) .world-content a:active {
  background-color: #FFF;
  color: #000;
  text-decoration: #111 solid underline;
}

:root:has(.collapsible-block-unfolded[style=""] span.act10, .collapsible-block-unfolded[style="display: block;"] span.act10) div.world-content::before {
  content: "DAY PIGEON";
}

